/*Table structure for table `os_printcloud` */

CREATE TABLE `os_printcloud` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '公司云端存储ID',
  `uid` int(11) DEFAULT NULL COMMENT '用户ID',
  `username` varchar(80) DEFAULT NULL COMMENT '用户名称',
  `filename` varchar(80) DEFAULT NULL COMMENT '文件名称',
  `title` varchar(100) DEFAULT NULL COMMENT '文件标题',
  `content` longtext COMMENT '文件内容',
  `cloud_url` varchar(200) DEFAULT NULL COMMENT '云端连接URL',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

/*Data for the table `os_printcloud` */

insert  into `os_printcloud`(`id`,`uid`,`username`,`filename`,`title`,`content`,`cloud_url`,`create_time`,`update_time`) values (1,NULL,'admin',NULL,'注意事项','&lt;div style=&quot;white-space: pre-wrap; text-align: justify; line-height: 1.75; font-size: 14px;&quot;&gt;1.本系统现阶段只供出版社内部同志使用，初次使用前请向中版公司相关工作人员索取用户名及初始密码；&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px; text-align: justify;&quot;&gt;2.请您修改初始密码后妥善保存，请不要透露给与工作不想干的人士，用户名、密码丢失请写明原因重新索取；&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px; text-align: justify;&quot;&gt;3.登陆后的数据为保密数据，请不要随意传播，中版联对因数据外泄的纠纷概不负责；&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px; text-align: justify;&quot;&gt;4.本系统使用的最终解释权归中国版联所有。&lt;/div&gt;',NULL,'2018-12-17 14:57:45','2018-12-17 20:48:47');

/*Table structure for table `os_printcompany` */

