/*Data for the table `os_printcompany` */

insert  into `os_printcompany`(`id`,`name`,`thumb`,`title`,`address`,`code`,`email`,`phone`,`introduction`,`content`,`create_time`,`update_time`) values (1,'北京中版联印刷物资有限公司','/uploads/20181218/16bb19690fc4932583b6f0e1738b1cca.jpg',NULL,'北京市东城区朝阳门内大街甲55号0812室',100010,'123456@cnpubg.com','010-5411057','&lt;p style=&quot;text-align: justify; line-height: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;&lt;strong&gt;北京中版联印刷物资有限公司（简称中版联公司）&lt;/strong&gt;是中国出版集团公司投资控股的全资子公司，公司于2004年3月份成立，注册资本金为人民币2000万元，是具有独立法人资格的经营实体。&lt;/span&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;公司依托中国出版集团公司的资源优势，与国内外著名纸张生产企业及经营企业建立了良好的合作关系，创新发展理念，探索新的发展形式和内容。确立了中版联公司在纸张经营和印刷物资行业的市场竞争地位。&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: right; line-height: 2em;&quot;&gt;&lt;span style=&quot;color: rgb(192, 0, 0); font-family: 宋体, SimSun; font-size: 14px; text-decoration: underline;&quot;&gt;点击查看更多&amp;gt;&amp;gt;&lt;/span&gt;&lt;/p&gt;','&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;&lt;/span&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;strong style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-align: justify; white-space: normal;&quot;&gt;北京中版联印刷物资有限公司（简称中版联公司）&lt;/strong&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-align: justify;&quot;&gt;是中国出版集团公司投资控股的全资子公司，公司于2004年3月份成立，注册资本金为人民币2000万元，是具有独立法人资格的经营实体。&lt;/span&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;中版联公司由中国出版集团公司发起设立，股东单位包括人民出版社、人民文学出版社、商务印书馆、中华书局、中国大百科全书出版社、中国美术出版总社、人民音乐出版社、生活﹒读书﹒新知三联书店、中国对外翻译出版公司、东方出版中心、中国出版对外贸易总公司、世界图书出版公司十三家国家级出版单位。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;公司业务范围：&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;经营国产及进口各类纸张、纸桨、装祯材料；代理各类印刷设备及印刷辅料。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;公司发展范围：&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;按照现代企业制度的要求，积极实施品牌战略，提升创新能力和社会 化服务能力，努力营建“服务一流、管理一流”，充满生机活力的现代企业。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;公司依托中国出版集团公司的资源优势，与国内外著名纸张生产企业及经营企业建立了良好的合作关系，创新发展理念，探索新的发展形式和内容。努力扩展业务的深度和广度。确立了中版联公司在纸张经营和印刷物资行业的市场竞争地位。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;strong style=&quot;text-indent: 2em;&quot;&gt;公司经营理念：诚信、务实、创新、高效&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;','2018-09-10 11:30:48','2018-12-18 14:05:01');

/*Table structure for table `os_printculture` */

CREATE TABLE `os_printculture` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '文化表ID',
  `title` varchar(100) DEFAULT NULL COMMENT '文化表标题',
  `source` varchar(200) DEFAULT NULL COMMENT '文化来源',
  `url` varchar(200) DEFAULT NULL COMMENT '文化表url',
  `cid` smallint(5) unsigned NOT NULL COMMENT '分类ID',
  `introduction` varchar(255) DEFAULT '' COMMENT '简介',
  `content` longtext COMMENT '内容',
  `author` varchar(20) DEFAULT '' COMMENT '作者',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '状态 0 待审核  1 审核',
  `reading` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '阅读量',
  `thumb` varchar(255) DEFAULT '' COMMENT '缩略图',
  `photo` text COMMENT '图集',
  `is_top` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否置顶  0 不置顶  1 置顶',
  `is_recommend` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否推荐  0 不推荐  1 推荐',
  `sort` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `publish_time` datetime DEFAULT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8 COMMENT='公司文化表';

/*Data for the table `os_printculture` */

insert  into `os_printculture`(`id`,`title`,`source`,`url`,`cid`,`introduction`,`content`,`author`,`status`,`reading`,`thumb`,`photo`,`is_top`,`is_recommend`,`sort`,`create_time`,`update_time`,`publish_time`) values (12,'纸张虽然生活中很常见，可是它的这些知识，你不一定知道',NULL,'',1,'纸张虽然生活中很常见，可是它的这些知识，你不一定知道','&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在我们平日里的工作生活学习中，纸张的使用是非常普遍的，但是也许你一直在使用它，却不是很了解它，所以今天就跟着小编一起去探索吧！&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸的发展史&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;中国的纸，从以前到现在大部分都是用树皮制造的，是可以折叠，也可以在上面写字的一种常用的生活用品。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸到现在已经有两千二百多年的历史了，在中国西汉的时候就已经出现了纸，但是由于技术的问题，他还不是很光滑，所以，在当时不能被很好的传播应用。在东汉的时候，有一个名叫蔡伦的人，将它做了改进，由此，蔡伦也被称为是中国造纸术的鼻祖。他所发明的造纸术，并不是按照植物原有的排列，反而是把他们打乱，然后交叉排列。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在古代，造纸术的产生，给中国从古至今都体现出了至关主要的作用，他给大家的生活带来了便利，同时也促进了许多优美的中国文化的传播和传承。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在中国，乃至世界上最早的纸张就是西汉的浆纸。在中国西汉刚开始的时候，中国的造纸术应该已经基本上成熟了，所以西汉时候的纸，按道理来说，质量也是不错的。但是由于现在的资料保存的都不是很完整，所以造纸的流程，在这里就不能详细的告诉大家了。现在可以告诉大家的是它的步骤特别的多，因为每一个东西的发展，他都是由复杂到简单的一个变化。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在古代，纸的种类也是很多的，用途也是非常的广泛，除了可以写字画画以外，像我们古代用的银票也是用纸做的，送他的时候把它称作交子。在明朝和清朝的时候，充满智慧的中国人，就把他们当做壁纸，有的甚至来剪纸，因为看上去非常的漂亮，所以他不仅在中国流行，甚至流传到了外国。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸的种类&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸的种类大约分下面几种。总的来说可以分为原料纸张和再生纸。原料纸张就可以称之为是我们的日用纸张。而再生纸对我们来说就非常的好。因为现在大家都提倡环保，而再生纸就是将以前用过的纸加以回收利用。再生纸不仅可以保护生态环境，还能节约能源，减少污染，如果我们的眼睛长期对着再生纸的话也可以减少阅读的疲劳。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;选择利用&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在使用纸张的时候，我们也应该恰当的选择。因为这个有利于保证我们看到的东西的质量，还能降低他的成本。也能更好的降低纸张的消耗。那我们大家在选择纸张的时候也要注意它的品种，规格和质量。但千万不能只看中一个，一定要全方位的综合考虑。在选择纸张的时候，尤其是在印刷的时候，我们要看所选纸张的品种。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;接下来要给大家普及一个知识，那就是全开纸。什么是全开纸呢？全开纸就是按国家规定标准生产的纸张。像这种纸张可以裁剪，也可以把它叠成面积相等的若干个小张，然后装订成册，这就是大家平时称的多少开本儿。像这种踩点的方法，国家也都有统一的标准。在平时就可以看出，像我们平时印制出来的图书，大部分的规格都是一样的。就是这种原因。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;同时开本也有好多种。大家平日里常见的开本是16开，32开和64开。然而，我们最最常用的应该是32开。不难看出他们大部分都是以二的几何数来裁的。像这种裁剪的方法，我们也应该知道。他们就是指横开法、直开法以及一些大家不是很常用的其他开法。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;选择纸张大部分是用来印刷的。那当我们印发的时候再选择纸张填的我们要注意纸张的大小，还有尺寸，还要适度的对他进行测量。一般只要满足印刷和使用者的要求，还是尽量选择大小比较小一点的纸张，因为那样可以降低印刷成册书本的成本。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;那么当我们确定了纸张的大小以后呢？我们也要对纸张的质量作出选择。这个时候就要求我们熟悉各种各样的造纸厂，这就包括熟悉他们的产品特点和它生产出来的纸的质量，并且也要随时随地的了解他的情况。等这些大小和厂家规格都确定之后呢，那我们在以后的印刷当中就尽量选择在这一个厂进行。特别是印刷同一本书，更应该在同一个厂家进行。如果半截换厂家的话会导致，有的印刷书籍的色差比较大。会大大的减少销售量。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在我们的生活中，每时每刻都能看到纸。例如，我们一般看的书，他们一般采用的都是凸版印刷纸，像这种类型的纸有卷筒和平板之分，它的种类有特号，一号，二号三种。可号和一号，一般用来印刷比较高级一点的书，像平时的书一般都用二号的凸版纸。还有就是报纸。报纸有他专用的新闻纸，也称白报纸。他也有卷筒和平版两种。那还有像我们平时要印刷的高级彩色胶印产品和宣传画、年画等，我们就要用到胶版印刷纸，这种纸有双面胶版纸和单面胶版纸之分。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;还有大家也可以看出，有的书皮和里面的是不一样的。像这种书，她的书皮一般是用专门儿的书皮纸制作而成的。因为书皮纸有很多种颜色，这样印刷不同的封面就很方便了。另外就是我们用的字典。印刷字典也有专门的字典纸。这种纸是专门儿为了印刷字典，手册等使用的。之所以选择他，是因为它的吸湿性比较强，非常耐潮。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;另外，就是，像会计门所用的复写纸，这种纸的专业术语叫做拷贝纸，他一般是呈半透明的状态。是会计工作者在会计工作时的不二选择。在书刊印刷当中，它也可以起到保护纸张的作用。接下来给大家介绍的一种纸叫做板纸，顾名思义，他就是像板子一样厚。像我们一般做的精装书，或者画册的时候，所用的就是这种纸。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;不管是什么纸，他一般都是一次性的，所以大家一定要珍惜纸张哦，这同时也是为保护环境出了一份力。&lt;/span&gt;&lt;/p&gt;','百度',1,0,'/uploads/20181217/0aa872b418f250cb05f7d33ef767e2f3.jpg',NULL,0,0,0,'2018-09-13 10:31:06','2018-12-17 16:57:37','2018-09-13 10:30:47'),(13,'如何看待纸张与环保之间的关系',NULL,'',1,'如何看待纸张与环保之间的关系','&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;越来越多的人意识到造纸给环境带来的危害,人们对环保类 用纸的呼声也越来越高了。其实，有两种方式可以减少造纸业给&amp;nbsp; 环境带来的危害:一种是向顾客提供环保类的纸张，这时我们作为 供应商或代销者;另一种是尽量购买和使用环保类用纸，这时我们 是顾客或消费者。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;一般情况下，我们都是消费者，毕竟消费者占绝&amp;nbsp; 大多数。其实我们的作用是最大的，因为顾客的购买最终会决定 生产者的供应计划。下面就是作为纸张购买者或消费者为环保做贡献时应该采取 的几个原则和方法。① 树立“环保人人有责”的责任感。在购买纸张时，要树立一 种环保的责任感，尽量购买环保类的纸张。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;一类是经过森林认证的纸张，一类是废纸再生的纸张。尽量少用由100%原浆纤维制造 的纸或纸制品，如新闻纸、包装纸和办公用纸等;最好购买造纸材 料中废纸含量高的纸张。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;① 最大化节省纸张。一定要杜绝滥用和浪费纸张的习惯。对 于印刷业来讲，通过改进印刷机在印刷准备方面的功能，减少在印 刷准备和生产中产生的废纸量。对于使用纸张的人，尽量减少纸&amp;nbsp; 张的用量。例如，尽量购买默认是双面的复印机、打印机以及传真 机等;提高每一份纸张型文件的使用效率;在打印前仔细检查设计 结果，避免因修改而造成的打印或复印上的浪费;不要滥发信件;&amp;nbsp; 延长出版物的使用周期等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;② 选用原浆纸张时要谨慎。使用的原浆纸张一定要是获得 FSC认证的企业生产的。FSC是（Forest Stewardship Council)是世 界上唯一公认的监督造纸厂使用再生林造纸的森林保护组织。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;③ 优先选用无氯的纸张。在符合要求的纸张中,要尽量选用无 氯化或无氯化物的品种。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;虽然现在无氯的纸张不太多，但是只要很 多坚持购买和使用这种纸，相信不久这种类型的纸就会多起来的。④ 多向周围人们传播环保概念。在一些文件上打印上环保纸 的特点和给环境带来的好处，唤醒人们的环保意识和责任感。总之，只要我们更多地购买和使用环保纸，各个造纸企业最终&amp;nbsp; 会将精力投向环保纸市场的。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;由于成本、技术等因素，从普通纸向 环保纸转变需要一个过程，不过只要努力，相信未来环保纸必然会 取代普通纸。&lt;/span&gt;&lt;/p&gt;','新浪',1,0,'/uploads/20181217/92bad9d029ab24432e6b2b96a3cd25d8.jpg',NULL,0,0,0,'2018-09-13 10:33:47','2018-12-17 21:47:07','2018-09-13 10:31:12'),(14,'本色纸真的比白色纸更健康吗？实验真相让你大吃一惊','','',1,'本色纸真的比白色纸更健康吗？实验真相让你大吃一惊','&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;近两年，一种浅黄色泽的本色生活用纸开始“挑战”白色卫生纸，在超市，本色纸的售价比白色卫生纸高出两到三成，但是依然有很多人购买，原因是看中其无任何漂白剂、更环保的特性。那么，本色纸真的和白色纸有那么大的区别吗？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;本色纸真的比白色纸更好吗？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;① 原材料不同&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;一般白色纸用的是木材原浆，而本色纸用的是秸秆原浆。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;② 生产过程不同&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;本色纸生产过程少了漂白剂，多了柔软剂和湿强剂。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;生产白色纸要漂白纸浆，而生产本色纸则无需漂白。漂白是要使用含氯的化合物的，所以少一个步骤会让人感觉更健康，关键是保护环境。不过，只要你购买的是正规厂家的纸巾，就无需担心残留问题。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;但未经漂白的纸浆木质素含量较高，生产出来的纸会发硬，而且粗糙，耐水性差，沾水容易烂，因此要比白色纸添加更多的柔软剂和湿强剂。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;③ 纸张性质&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸张性质还真没有特别大的差别。据黑龙江网络广播电视台一次求真实验，实验员对4种本色和白色纸巾样本的横向抗张指数、纵向湿抗张强度、柔软度分别进行检测，发现4种纸巾都是合格的，而且没有太大差异。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;本色纸真的比白色纸更健康吗？实验真相让你大吃一惊&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;卫生纸，能用来擦嘴么？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;许多人认为面巾纸和卫生纸是一样的，都是纸，本是同根生，何必分彼此呢？其实国家对它们制定的标准有着很大的不同。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;① 原材料要求不同&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;上餐桌用的面巾纸、厨房用纸，只能使用新生浆，不能使用废纸浆。而产品包装标有100%原生木浆就是指造纸的木浆都是新原料。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;而厕所用纸则可以使用废纸浆，比如办公用纸的废纸浆，就可以用来生产卫生纸。纯木浆中就允许存加再生材料。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;② 菌落数要求不同&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;面巾纸直接接触面部，对卫生要求十分严格，每克面巾纸含菌量不得高于200个单位，大肠菌群、金黄色葡萄球菌的要求二者均是不得检出。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;而卫生纸的要求就相对宽松，每克卫生纸含菌量不高于500个单位就算合格了。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;③ 荧光剂要求不同&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;面巾纸上要求“无可迁移性荧光增白剂”；对卫生纸则没有此项要求。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;④ 纸张性质不同&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;卫生纸的纤维比较容易断裂，入水浸泡后，容易分解，变成小碎块，丢进马桶，也不会堵住马桶。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;面巾纸要求具有较高的湿柔韧性，能够较快地吸水，遇水不易分解成碎纸屑，因此扔入马桶容易造成下水管道堵塞。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;所以，不是所有纸都是可以拿来擦嘴的！&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;本色纸真的比白色纸更健康吗？实验真相让你大吃一惊&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;如何确定你所购买的是面巾纸？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;按照中华人民共和国国家标准，「纸巾纸」包括擦脸擦嘴用的面巾纸、餐巾纸，包装上的「执行标准」写的是 GB20808，要求细菌菌落总数低于 200 cfu/g。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;而卫生纸，也就是厕纸，比如各种卷纸，它的「执行标准」是 GB20810，要求细菌菌落总数低于 600 cfu/g 就行。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;这两个标准是国家标准化管理委员会发布的，其中GB/T20808的最新版本为GB/T 20808-2011（2012年7月1日开始实施）、GB20810的最新版本为GB 20810-2006（2007年6月1日开始实施）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;所以我们在购买面巾纸的时候，不要只看包装，而是要记看执行标准的编号。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;优质纸巾怎么选？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;1、看原料&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;100%原生木浆和100%纯木浆一样吗？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;原生木浆和纯木浆虽然听起来差别不大，但概念完全不同，原生木浆纸用的是新原料，而纯木浆纸中可能混有回收或者再生的二手原料。所以，尽管被标上了100%可信赖的标签，但作为聪明的买家，你肯定能选择到最好的那一款。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2、摸手感&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;好的纸巾木浆含量高，摸上去手感细腻柔软，不易掉粉、掉毛。不好的纸巾纸质偏硬，比较松散，易掉粉、掉毛，好纸巾有益养生。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;3、比韧度&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;手里拿一款原木浆纸巾，稍用力拉扯，纸巾会有被拉扯的褶皱出现，但并不会断裂。而不好的纸巾由于木浆含量低，柔韧性差，稍微用力即会出现断裂现象。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;4、观察燃烧情况&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;添加过量荧光剂的纸巾燃烧后纸灰呈白色或黑色，而正常合格的纸巾燃烧后为自然的灰色，劣质餐巾纸一烧会有油。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;5、测试浸泡状态&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;好纸巾纸密度高、韧性大，即使浸泡在水里，也不会变形、松散。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;小小纸巾，也关系到每个人的日常健康，忽视不得。为了自己和家人的健康，赶紧看看家里的纸巾是不是符合标准吧！&lt;/span&gt;&lt;/p&gt;','河源日报',1,0,'/uploads/20181217/a0bf3df0ed93e4262599ee08ba178744.jpg',NULL,0,0,0,'2018-09-13 10:38:52','2018-12-17 16:45:11','2018-09-13 10:37:00'),(15,'2018中国造纸业发展现状','','',1,'2018中国造纸业发展现状','&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2018年中国造纸行业发展现状：供给侧改革趋严，马太效应凸显；成本端高位下，龙头纸企盈利能力持续增长。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;一、长逻辑：供给侧改革趋严，马太效应凸显&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;供给侧改革驱动下，造纸行业去产能效果明显，供需格局进一步改善。造纸行业由于污染严重且产能过剩问题突出，成为供给侧改革的重点去产能产业。在前期粗放型发展方式下，造纸行业中小企业数量多占比大，出现了供过于求、技术水平和生产方式落后、污染严重等问题，2011年，工信部和林业局联合发布《造纸工业发展“十二五”规划》推动行业供给侧改革。“十二五”期间，行业共淘汰落后产能3872万吨，这一数量是2016年全年产量的31.43%，“十三五”计划造纸行业将继续淘汰800万吨落后产能。同时，受经济放缓趋势影响，行业市场需求下行，造纸企业的固定资产投资支出出现下降，2012年以来固定资产投资额累计年增速仅为5.67%，未来新增产能增速将放缓。在产能出清和新增投资收缩的推动下，造纸产销量增速均出现明显下滑，2015年产销量接近零增长，行业供需格局得到改善。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2018中国造纸业发展现状及发展前景分析&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;二、成本端高位下，龙头纸企盈利能力持续增长&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;1、包装纸淡季不淡，纸价上行龙头企业盈利维持高位。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;进口废纸政策趋严导致外废进口量减少。2017年以来，我国陆续推出关于废纸进口的管控政策，包括针对未经分拣的废纸禁止进口、进口废纸的企业规模为5万吨/年以上、进口废纸含杂率标准降为0.5%、对美废100%开箱检验等，限制了废纸进口量。同时，进口废纸的环保执法力度也日益趋严，3月以来全国海关组织开展打击洋垃圾走私的“蓝天2018”专项行动，港口外废不合格退运事件频频发生，综合退运量已超万吨。受进口废纸趋严形势影响，我国进口废纸的总量下降，2017年全年废纸进口累计值为2572万吨，同比下降9.80%，2018年随着废纸新政的进一步实施，预计全年废纸进口总量将进一步收缩。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;原材料价格持续上涨推动包装纸价格持续上行。在外废配额严格限制、检查形势趋严、外废批额降低的情况下，2018年我国进口废纸量大幅减少，截止4月底2018年废纸累计进口量为512万吨，累计同比下降49.0%。废纸供需失衡推动国废价格的上涨，截止6月7日，国废均价为2998元/吨，相较年初的2473元/吨上涨幅度约21.21%，与2017年同比增长65.22%。成本端压力较高推动下游成品纸价格上涨，2018年以来多家纸厂纷纷发布涨价函。截止6月8日，箱板纸最新平均市场价为5527元/吨，较年初上涨16.35%，瓦楞纸最新平均市场价为4845元/吨，较年初上涨24.23%。预计废纸价格的持续上涨将为箱板瓦楞纸价格提供支撑，2018年包装纸价格继续上行。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;行业库存维持在低位。2018年以来，箱板瓦楞纸企业库存走势一致，呈小幅波动趋势，但整体维持在低位。4月箱板瓦楞纸企业库存天数为15.5天，与去年同期相比减少了3.24天。由于供给侧改革叠加环保政策趋严，落后产能出清导致行业供给端收缩明显，库存消化快，同时叠加旺季来临下游包装厂采购积极性高，未来行业库存预计维持在低位。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;包装纸进入行业旺季，供需紧平衡推动纸价上行。供给端来看，供给侧改革持续推动落后产能出清，根据智研咨询数据，2012-2016年箱板瓦楞行业淘汰产能580万吨，总淘汰产能达到2016年产能的9.42%，中国造纸协会指出，随着环保政策趋严，“十三五”期间行业将继续淘汰落后产能800万吨，根据“十二五”期间的纸种淘汰产能占比计算，预计十三五期间将淘汰约147万吨包装纸落后产能。新增产能方面，2017-2018年将有部分新建包装纸项目投产，据统计，2017年，国内箱板瓦楞纸市场新投产产能约440万吨，预计2018年将新增包装纸产能500-600万吨，但目前来看，箱板瓦楞纸新增产能主要在下半年释放，2018年整体产能供给偏紧。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2018中国造纸业发展现状及发展前景分析&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2、木浆价格维持高位运转，文化纸供需格局改善&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;从供给方面来看，全球纸浆供应量略有增长。我国是全球木浆主要消费国之一，木浆以进口为主，进口木浆量占比达60%-70%。随着宏观经济预期转好，2017年以来全球木浆新增产能逐渐落地，根据中华纸业网统计，针叶浆2017年和2018年全球投放产能分别达137万吨和102.5万吨，阔叶浆2017年和2018年新增产能为190万吨和236.5万吨，每年新增产量约占全部纸浆产量的1.7%，产能增加有限。截至6月8日，浆价已位于历史高位，国内木浆平均价达6255.25元/吨，同比增长25.41%，预计后期浆价继续上涨的可能性不大。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;从需求方面看，我国木浆需求稳定增长，支撑木浆价格。一方面，随着经济形势的复苏，木浆需求端纸张景气较高，我国木浆消耗量稳步增长，2014-2016年增长率保持在6%-7%;另一方面，在供给侧改革和环保执法下造纸企业竞争格局得到优化，随着落后产能和高污染产能的退出，大企业生产步入良好态势发展，未来对于木浆的需求将持续增加;替代性方面，由于废纸进口政策对于废纸价格和数量的影响，木浆需求将进一步上升，为木浆价格提供强支撑，因此我们预计未来木浆价格将维持高位。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;文化纸传统旺季叠加浆价上涨，文化纸价格高位运行。2018年以来，受废纸新政和木浆供需格局影响，内外盘浆价均处于历史高位，成本端压力下木浆系纸种价格整体呈现上升趋势，同时1-5月为文化纸传统季节性旺季，市场需求推动文化纸价格高位运行。截至6月8日，铜版纸平均市场价为7133元/吨，双胶纸平均市场价为7333元/吨，铜版纸较年初下降了1.39%，双胶纸较年初上涨了3.28%，二者同比增长5.41%和14.73%。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;3、文化纸供需格局改善。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;供给端，铜版纸和双胶纸2018年无新增产能，且在供给侧改革影响下中小企业产能出清，铜版纸2012-2016年每年产能和产量均呈递减趋势，双胶纸在波动中略有下降。文化纸行业的集中度上升，目前铜版纸行业CR4达到86.78%，产能分布较为集中和稳定，双胶纸行业CR4为45.25%，仍有较大的提升空间，总体文化纸的竞争格局得到优化。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;需求端，铜版纸双胶纸的需求目前均逐渐企稳。铜版纸行业景气度逐渐改善，2012-2016年，由于电子媒体的发展以及产能过剩问题突出，铜版纸的生产和消费增长几乎停滞，通过转产、停产等措施，铜版纸的供给端收缩，2017年需求达585万吨，销量同比增长3.54%，下游需求回升。未涂布印刷书写纸供需格局转好，受供给侧改革、环保政策和原材料影响，双胶纸供给呈现收缩趋势，需求方面2016年几乎无增长，2017年需求为1744万吨，同比增长3.26%。文化纸的消费有一定的季节性特征，进入下半年以后，夏季啤酒旺季有助于推动作为酒标的单铜纸的需求上升，9-12月为年终备货期，各大商家印刷单页等进行宣传促销将带动双铜纸的销量增长;双胶纸方面，9月的春季教材备货期，年底各大公司报表的印制将催生大量需求。综合来看，文化纸在产能收缩的情况下，需求随国家经济形势稳步回升，供需格局进一步改善。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2018中国造纸业发展现状及发展前景分析&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;4、生活用纸需求结构升级，静待竞争格局优化。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;生活用纸2018年以来价格高位运行且波动幅度较小。受原材料纸浆价格上涨影响，生活用纸价格在2017年8月份-12月份大幅上涨1500元/吨。随后在2018年初，纸浆价格的下调使得生活用纸价格开始小幅下降，但整体仍维持在8400元/吨，波动幅度较小。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;生活用纸仍供大于需，随着需求的稳定增长供需格局将得到改善。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;从供给端来看，目前仍处于产能过剩阶段。2012年以来中国生活用纸行业迅速发展，产能从2012年的784万吨到2017年的1295.7万吨，复合增长率8.73%，产量从2012年的627.3万吨到2017年的956.3万吨，复合增长率高达7.28%，目前产能利用率保持在75%-80%左右。相比较来说，生活用纸的消费量年复合增长率为6.95%，产销缺口2012-2015年持续扩大，产能的扩张大于需求消费量的增长，使得目前处于供给相对过剩的阶段。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;从需求端来看，我国生活用纸消费量快速上升。生活用纸作为刚性消费品，与国家GDP的增长、人口增速、生活质量的提高等因素密切相关。在消费升级、人均GDP的提高和生活水平的改善等因素的推动下，生活用纸行业获得了持续的动力，2012年以来，生活用纸消费量复合增长率为6.95%，我国生活用纸国内市场规模持续扩大，2016年已达到988亿元，同比增长12.06%。生活用纸人均消费量2017年为5.94kg/年，虽然保持了9.6%的增长，但同发达国家和地区仍有较大差距，随着二胎政策的推广、国内消费需求的多元化和高端化等推动，我国生活用纸消费量未来发展潜力巨大。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;生活用纸产业结构优化升级。随着生活水平的提高和消费升级的驱动，生活用纸的产业结构将不断优化升级。对比2012年与2017年生活用纸产品消费结构来看，卫生纸占比由62%下降到53.7%，高毛利的手帕纸、面巾纸、餐巾纸等高端生活用纸占比明显提升，人均消费量复合增长率分别为8.05%、10.73%、10.53%，高于市场整体增速。(转自：中国产业信息网）&lt;/span&gt;&lt;/p&gt;','中国产业信息网',1,0,'/uploads/20181217/c25f04bca0d52cef416c4e38c12185a2.jpg',NULL,0,0,0,'2018-09-13 10:39:36','2018-12-17 16:24:10','2018-09-13 10:39:04'),(16,'2018-2019年度中版联公司 纸张采购招标情况说明',NULL,NULL,9,'2018-2019年度中版联公司 纸张采购招标情况说明','&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2018年11月8日-11月9日，北京中版联印刷物资有限公司组织召开了年度纸张采购公开招标终评会，集团办公室主任李红强、人民文学周绚隆、宋佳月；商务印书馆王齐；中华书局周清华、张宇；人民 出版社栾文驹；人民音乐出版社陈晓燕；三联书店卢岳；天天社王超、世图潘燕；商务国际韩秋红；中版联公司陆南宸；股份公司财务部湖泊潇；顾问孙建华作为评委参会；会议主持人张九国以及工作人员共计23人与会。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;本次会议是集团上市后的第一次公开招标会议，集团领导非常重视，多次开会研究强调：本次招标工作必须秉承公开、公正、公平的原则进行，决不能流于形式；评标、议标必须合法、合规、合理，坚决杜绝违规行为，确保招标结果能取得良好的社会效益与经济效益。中版联公司按照指示，从零起步，于2018年6月份制定了招标文件，历经三个多月的研磨和修改，最终确定了招标形式与评标专家库，并于10月10日进行报名企业的初评，筛选出54家企业进入终评会议。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;会议有供应商33家，纸张生产厂家18家，共计51家企业参与本次的终评竞标。本次招标与以往的采购方式的最大区别在于价格并不是竞争的唯一条件，要从投标人的履约能力、技术、服务以及产品性价比等因素进行评判，根据终评评判标准，评委需从投标企业的服务水平、价格条件、企业资质、产品质量等量化标准进行严格评标。会议为期两天，于金泰绿洲酒店多功能厅举行，会议内容如下：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;一、核实投标人身份，现场公开开标。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;工作人员对投标代理人进行核实，确定与投标人身份一致后，全体投标人统一进入会场，按照投递标书的先后顺序当场核验投标文件的密封情况，在投标人确认投标文件密封完好无损的情况下公开开标，投标人对标书中的承诺书部分和价格部分进行公开展示，所有标书展示完毕后，投标代理人候场等待接受评委咨询。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;二、投标人描述企业情况，接受评委质询。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;投标人按照投递标书的顺序进入会场进行3-5分钟的企业描述。同时，由财务专家严格把关，对参与投标的企业的运营状况和财务报表进行现场审核，特别是从纸张价格、最长可接受账期、是否接受承兑汇票三个方面进行了综合细致的评议。随后，全体评委对企业资质、技术服务、产品质量、产品性价比等因素进行质询。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;三、评委进行复议，对投标企业进行评分。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;评委团现场经过讨论，明确了评分规则和复议内容，形成统一的认识，获得统一的评价标准，以保障评标流程的公平与公正，在复议过程中，评委积极参与发言，提出合理有效的建议，全体评委意见达成一致后对投标企业进行评分，并在评分表上签字确认。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;四、对评分结果进行排名&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;随后，由财务人员对评分表进行收取、校准、统计、并进行排名。期间，中华书局张宇同志表示：由于纸张供应商和生产厂家的经营方式有区别，通过这次招标的实践操作后，建议以后评分标准应做出更细致的调整。商务印书馆王齐同志强调了独特产品的特殊性和企业对客户服务的重要性。人民文学周绚隆同志、三联书店卢岳同志、外聘专家栾文驹同志、顾问孙建华同志对投标企业的技术标准、物流配送、售后服务等因素提出许多好的意见和建议。会议在大家的努力下有条不紊的进行，直至全体投标企业排名完毕。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;经过整整两天的紧张工作，最终确认了全部参与投标企业的排名（排名结果见附件），排名结果将上报集团纸张整合工作委员会做最终中标结果的确认，会议发起人，中版联公司副总陆南宸同志向各位评委对招标工作的认真负责的工作态度表示感谢。此次会议为中版联公司明年的工作夯实了基础、指明了方向，相信在集团领导的带领下、各位同志的辛勤工作下，中版联公司将会以更好的服务水平和更务实的工作态度为集团贡献自己的力量！&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2018-2019年度纸张公开招标终评会圆满结束。&lt;/span&gt;&lt;/p&gt;','JZY',1,0,'/uploads/20181216/b591af0008403c2a28bde9f4d89428f6.jpg',NULL,0,0,0,'2018-09-13 10:43:11','2018-12-17 16:03:59','2018-09-13 10:42:55');

/*Table structure for table `os_printgroup` */

CREATE TABLE `os_printgroup` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '集团动态ID',
  `title` varchar(100) DEFAULT NULL COMMENT '集团动态标题',
  `source` varchar(200) DEFAULT NULL COMMENT '集团动态来源',
  `url` varchar(200) DEFAULT NULL COMMENT '集团动态url',
  `cid` smallint(5) unsigned NOT NULL COMMENT '分类ID',
  `introduction` varchar(255) DEFAULT '' COMMENT '简介',
  `content` longtext COMMENT '内容',
  `author` varchar(20) DEFAULT '' COMMENT '作者',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '状态 0 待审核  1 审核',
  `reading` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '阅读量',
  `thumb` varchar(255) DEFAULT '' COMMENT '缩略图',
  `photo` text COMMENT '图集',
  `is_top` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否置顶  0 不置顶  1 置顶',
  `is_recommend` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否推荐  0 不推荐  1 推荐',
  `sort` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `publish_time` datetime DEFAULT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8 COMMENT='集团动态表';

/*Data for the table `os_printgroup` */

insert  into `os_printgroup`(`id`,`title`,`source`,`url`,`cid`,`introduction`,`content`,`author`,`status`,`reading`,`thumb`,`photo`,`is_top`,`is_recommend`,`sort`,`create_time`,`update_time`,`publish_time`) values (8,'守正创新——中国出版集团庆祝改革开放40周年书画摄影展开幕式暨2018年香山论坛大会成功举办','','',2,'守正创新——中国出版集团庆祝改革开放40周年书画摄影展开幕式暨2018年香山论坛大会成功举办','&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;12月13日上午，“守正创新——中国出版集团庆祝改革开放40周年书画摄影展”开幕式暨2018年香山论坛大会在荣宝斋大厦成功举办。刘伯根副总裁出席大会并讲话。各单位领导、党办、工会办、团组织负责人和团员青年代表约150人参加大会。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;刘伯根在讲话中指出，书画摄影展、香山论坛和青年创新大赛以及年中举办的集团第二届职工运动会等活动，都是集团庆祝改革开放40周年的系列安排。今天年轻人的发言都是根植自身岗位实践探索进行的有益思考和经验归结，水平和质量有深度有宽度，集中体现了集团青年对中版事业的不懈追求，这是集团实现高质量发展的重要推动力。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;刘伯根强调，按照习近平总书记关于央企发展定位要求，集团要做国际一流企业，而“三型集团”恰好符合新时代发展形势和发展战略要求。要牢牢抓住导向不放，切实把中央要求和社会效益把握好结合好，时刻提高学习领会中央精神的能力和政策理论水平，全面理解主流出版、融合发展、国际传播的战略主旨和相互促进关系，把“三型集团”建设作为推进高质量发展重要战略路径，从而推进国际一流企业建设目标的实现。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;会上，11位来自基层的青年围绕“三型集团”建设要求，特别是融合发展主题展开精彩交流发言。宣读了2018年香山论坛征文表彰决定和2018年青年创新大赛评选结果并为获奖者颁奖。书法、绘画和摄影作者代表分别进行了发言。本次展览共展出职工创作的书画摄影作品302幅。展览持续到18日。&lt;/span&gt;&lt;/p&gt;','admin',1,0,'/uploads/20181214/b325fc74a7b6ddeb2743787f4db7bfbc.jpg',NULL,0,0,0,'2018-09-11 15:46:58','2018-12-14 12:44:03','2018-09-11 15:46:03'),(9,'中国出版集团与孔子学院总部“联姻”','中国出版集团','',5,'中国出版集团与孔子学院总部“联姻”','&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;12月5日，在第十三届孔子学院大会（四川成都）上，中国出版集团党组成员、中国出版传媒股份有限公司副总经理李岩与孔子学院总部副总干事马箭飞签订“孔子学院合作伙伴计划”协议，与孔子学院总部确立战略“合作伙伴关系”。集团国际部主任刘忠、商务印书馆总编辑周洪波、中译出版社社长张高里等出席。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;“孔子学院合作伙伴计划”是孔子学院总部为促进中外人文交流、推动世界多元多彩的文明发展、鼓励社会各界积极参与孔子学院建设而推出的新举措。该计划既有中长期战略合作计划，又有具体项目合作方案，内容涉及中外人文交流等多领域，包括企业捐资捐赠合作办学、联合培养人才、提供实习机会和就业岗位、开展文化交流活动、孔子学院数字化建设、共建联合实验室等。通过“合作伙伴计划”，各国孔子学院将发挥遍及全球的语言教学和文化交流网络，与国内外合作伙伴一起打造开放包容、共建共享的公共平台,为国内外合作伙伴提供更好的发展空间。同时，国内外合作伙伴也将充分发挥各自行业优势和丰富资源，支持和帮助汉办以及各国孔子学院开展相关业务，共同为各国民众提供更加优质、便捷的汉语学习服务和文化交流体验。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;企业与孔子学院的“联姻”，企业参与中华文化传播，助力汉语国际教育，体现了企业的国家情怀、教育情怀、全球情怀。中国出版集团与作为出版“国家队”，在落实中国文化“走出去”这一国家倡导中，肩负着重任。多年来，中国出版集团牢记建设“国际著名出版集团”使命，积极响应“中华文化‘走出去’迈出更大步伐”，“讲好中国故事，传播好中国声音”，及“一带一路”倡议，将“走出去”和国际化发展作为全集团“三化目标六大战略”的基础性战略，并持续推进。这次与孔子学院总部建立合作伙伴关系，双方将在图书出版、数字化建设、汉语教学资源建设等领域展开充分合作，这将为集团“走出去”和国际化发展提供新的契机和机遇。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;据悉，孔子学院创办14年来，在坚持汉语教学的同时，积极拓展功能，仅2018年举办中医、艺术等各类文化活动4.4万场，受众1300万人；出版译作640多部，开展学术研究项目1600多个，组织各类高端学术会议论坛7000余场。49所孔子学院开设“汉语+”课程，内容涉及商务、旅游、中医等领域，学生超过1.2万人。331家中外企业参与孔子学院合作办学，有力助推了中国与各国的人文交流和经贸合作。目前，已有154个国家和地区建立了548所孔子学院和1193个课堂，各类面授学员累计1100万人。&lt;/span&gt;&lt;/p&gt;','中国出版集团',1,0,'/uploads/20181214/14bcabb18b56a0eeb89c2959778c4261.jpg',NULL,0,0,0,'2018-09-11 17:11:49','2018-12-14 12:27:29','2018-09-11 17:10:59'),(10,'孙月沐出席中国美术出版总社2019年度选题论证会暨编辑大会','中国美术出版总社','',5,'公司动态','&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;2018年12月3-5日，中国美术出版总社组织召开2019年度选题论证会暨编辑大会，中国出版集团公司党组成员、股份公司副总经理孙月沐，出版业务部主任刘祚臣、项目处副处长孙照海，总社领导班子出席选题会。总社全体中层干部、编辑及校对人员、新媒体公司、品牌中心、营销中心、出版部等相关人员参加了本次大会。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;孙月沐在讲话中，充分肯定了本次大会的重要成果，表示会议突显精气神，振奋了士气。对总社发展提出了具体要求，一是专业，要牢牢把握专业特色，努力做到专精深，以专业的方式完成出版使命；二是品质，既要有品，还要有质，要做反映时代的、有品质的主流出版；三是科技，要顺应时代潮流，注重科学技术，做好融合发展；四是市场，要有强烈的市场意识，深入市场调研，用市场成果作为检验出版的一个指标，着力解决“高原”多、“高峰”少的问题。他指出，选题论证是一个动态反复论证的过程，希望总社以认真负责的态度，走在专业出版前面，取得更好更大成绩。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;总社党委书记周伟在总结讲话中，提出导向金不换，质量高于天的工作要求，并部署了2019年度的选题出版工作。他总结了本次会议的六条共识，一是落实意识形态责任制，提高编校质量的共识；二是向市场要效益的共识；三是对总社发展紧迫感的共识；四是在现有基础上向教育、少儿、学术出版用力的共识；五是传统出版与新媒体融合的共识；六是在内容产业延伸上的共识。针对编校质量，他指出一是要进一步明确责任编辑的主体责任；二是加强对编辑、校对的培训考核；三是进一步提高校对部门的地位和待遇；四是编校质量检查规范化、制度化、常态化；五是所有质量问题和考核挂钩。他希望总社全体编辑都要具备有文化、有理想、有方向的基本素养，努力培养策划、编辑、写作、设计、经营、新媒体六个方面的能力。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;各编辑室分别介绍了本室2019年度主题出版类、优秀传统文化类及一般图书选题情况，总编室、营销中心、出版部分别通报了2018年度总社图书选题、销售及出版情况，校对组结合总社书刊编校质量情况对编校规范、具体稿件进行了分析和讲解，部分一线编辑分享了自己策划选题及编辑加工经验，各期刊主编及新媒体公司负责人分别介绍了2019年工作规划。会议各小组围绕三个方面重点议题进行讨论：一是打造“三型集团”，建设“四个人美”，坚持正确的出版导向，聚焦主业，在新形势下如何适应市场需求，策划和出版更多双效俱佳的图书？二是2019年如何进一步加强管理，提高书刊编校质量？三是如何进一步做好传统出版和新媒体的融合，加强内容建设？&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;集团公司刘祚臣主任，总社总编辑林阳，副社长杨辉，副总编辑欧京海，党委副书记、副社长高世屹分别提出了具体的要求。&lt;/span&gt;&lt;/p&gt;','赵军平',1,0,'/uploads/20181214/f55d418649c24bd368ab8aa72de9a80a.jpg',NULL,0,0,0,'2018-09-11 17:12:23','2018-12-14 12:22:46','2018-09-11 17:11:54'),(11,'刘伯根：高质量发展的路径是“破、立、优”','光明日报','',1,'高质量发展的路径是“破、立、优”','&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;伴随着文化产业的从小到大、从弱到强，一批具有国际竞争力的“文化旗舰”成长起来，一个文化板块正在资本市场崛起，一个新兴的文化产业正在成为国民经济支柱性产业。近日，在首届长三角国际文化产业博览会期间，中宣部召开了全国“30强”文化企业座谈会，骨干文化企业负责人就如何坚持守正创新，肩负文化使命，推出更多优秀文化产品，助推文化产业高质量发展发言。中国出版集团公司党组成员、副总裁刘伯根在座谈会上发表讲话，整理如下：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;最近一段时期，我们认真学习贯彻习近平总书记在全国宣传思想工作会议上的重要讲话，明确把高质量发展作为集团新时代的工作主线，并发布了高质量发展指标体系。在发展中，我们认识到,当下边际收益递减现象出现后，要把结构优化、新动能培育和全要素生产率提升作为主攻方向，把高质量发展作为主要目标。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;当前，制约实现高质量发展的主要因素，不是生产能力不足、要素投入不够，而是对新需求把握不准、对新渠道掌控不力、对新传播方式运用不精，根本上则是我们对市场新变化反应迟钝而带来的供需失衡。这种迟钝和失衡，既反映在产品结构上，更反映在投资结构、资产结构、业务结构、营收结构、人才结构等方方面面。因此，抓住优化资源配置这个重点，整体提高供给体系质量，以适应、满足和引导新时代的新需求，是实现高质量发展的方向。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;高质量发展的主要路径是破、立、优。所谓“破”，就是大力破除无效供给，处置“僵尸企业”，该“断奶”的就“断奶”，该止损的就止损，坚决拔掉“输液管”“呼吸机”，实现去无效低效产能、去无效低效库存、去无效低效杠杆，为优质企业、高效板块节省资源。所谓“立”，就是树立起创新这杆大旗，通过创新补齐资源短板、品质短板、人才短板、制度短板，通过培育新动能，为可持续发展不断形成新的增长点。所谓“优”，就是通过优化资源配置、优化政策供给、优化绩效评价等，进一步降低内部摩擦成本、提升资源使用效率，降低政策滞后成本、提升政策支持效率，降低内部分配成本、提升正向激励效率。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;建成具有全球竞争力的世界一流出版企业，是集团高质量发展的目标和标志。我们提出要“坚持一个统领，打造三型集团”，其内涵就是通过高质量发展最终建成世界一流文化企业，更好地服务党和国家工作大局，增强人民的文化获得感、幸福感。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;任何的宏伟蓝图，最终都要落实在具体指标上，出版业要建立高质量发展指标体系。要突出产业特殊性，坚持把社会效益放在首位，实现社会效益和经济效益的统一。要突出结构性，坚持问题导向，聚焦改革发展中关键的结构性问题，推动结构优化和发展质量变革、效率变革、动力变革。要具备引领性，指标值设置既要考虑宏观环境，也要考虑产业发展目标和战略定位，瞄准世界一流的定位来设置指标。&lt;/span&gt;&lt;/p&gt;','刘伯根（中国出版集团公司副总裁）',1,0,'/uploads/20181214/3f279d16fb386b6b2c7f5c01c77a7b41.jpg',NULL,0,0,0,'2018-09-19 10:39:22','2018-12-14 12:24:05','2018-09-19 10:38:17'),(12,'谭跃：坚持守正创新 推动高质量发展','中国出版集团','',1,'坚持守正创新 推动高质量发展。','&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;8月21日全国宣传思想工作会议后，中国出版集团公司高度重视、迅速响应，第一时间传达并部署深入学习贯彻习近平总书记在全国宣传思想工作会议上的重要讲话。8月27日，中版集团党组理论学习中心组集体学习了中央领导在全国宣传思想工作会议上的讲话精神；9月4日，召开全集团干部大会传达会议精神，并下发了集团学习贯彻全国宣传思想工作会议精神的31条具体举措。10月15日，集团党组理论学习中心组结合会议精神，集体学习《中央企业领导人员管理规定》和《中共中央组织部国务院国资委关于进一步明确中央企业党委（党组）对直属企业（单位）党建工作领导和指导责任的通知》；同日召开贯彻落实全国外宣工作推进会精神专题会，强化“大宣传”“一盘棋”意识，以“三个落实、九条举措”推进国际传播能力建设。10月16日中版集团干部警示教育大会上，集团公司传达贯彻中央和国家机关警示教育大会精神，充分展现党组狠抓党风廉政建设，全面从严治党的坚定决心。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;11月5日~7日，中版集团再次召开为期两天半的专题学习交流会，进一步贯彻落实全国宣传思想工作会议精神，并传达中共中央政治局常委、中央书记处书记王沪宁，中共中央政治局委员、中央书记处书记、中宣部部长黄坤明在新时代宣传思想工作创新研讨班上的讲话，更加全面、深入地进行学习和交流，并结合中央精神谋划2019年中版集团及各单位的重点工作。中宣部出版局局长郭义强出席会议。中国出版集团总裁谭跃作主旨讲话。中国出版集团公司、中国出版传媒股份有限公司领导刘伯根、李岩、潘凯雄、孙月沐以及总部本部各部门负责人、各单位领导班子成员150余人参会。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;郭义强对中国出版集团公司学习贯彻中央精神的系列工作给予肯定，并针对如何领会“守正创新”做了重点指导。他指出，出版工作中的“守正”即是要牢固树立“四个意识”，始终坚定“四个自信”，牢记“举旗帜、聚民心、育新人、兴文化、展形象”的使命任务，严格落实意识形态工作责任制，坚决做到“两个维护”，坚持正确政治方向、出版导向、价值取向，坚守文化立场、文化情怀、文化使命，坚持内容建设第一、提高质量第一、多出好书第一，坚持党管宣传、党管意识形态、党管出版。“守正”就是要更加自觉地把握大局、服务大局，更加自觉地统一思想、凝聚力量，更加自觉地提高质量、多出精品，更加自觉地厚植人才、固本培元，更加自觉地加强党建、筑魂塑形。出版工作中的创新体现在内容形式创新、形态业态创新、渠道载体创新、体制机制创新4个方面，要在跟上时代、适应变化中改进创新，在解放思想、拓宽思路中改进创新，在依托内容、用好科技中改进创新，在破解难题、务求实效中改进创新。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;郭义强提出，中国出版集团在谋划明年工作中，要紧扣传播传承先进文化这一根本任务，更好地履职尽责，发挥作用；紧扣提高作品质量的生命线，不断提高整体的出版能力和水平；紧扣改革创新的根本动力，加快集团的改革发展，不断增强实力和竞争力；紧扣导向正确，进一步严格出版管理；紧扣增强“四个意识”的核心，加强党的建设和思想作风建设。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;遵循“九个坚持”，牢记“十五字”使命&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;中版集团贯彻全国宣传思想工作会议精神学习交流会引起了与会者的热烈反响，“九个坚持”、“十五字”使命、“守正创新”是主题交流中反复提及的关键词。大家纷纷表示，中央会议既有统领性精神要义，又为具体工作指明了方向，振奋人心。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;谭跃表示，习近平总书记在全国宣传思想工作会议上的重要讲话，总结了宣传思想工作的重要规律和使命任务，提出了做好四项重点工作的明确要求，对做好新形势下宣传思想工作具有十分重大的指导意义。他表示，“九个坚持”不仅是对党的十八大以来宣传思想工作的规律性总结，也是做好今后宣传思想工作的总的原则性要求，集团各单位要牢牢把握和遵循；在新形势面前，要自觉担负宣传思想工作的“十五字”使命，坚持“举旗帜、聚民心、育新人、兴文化、展形象”；深刻把握“四项重点工作”的战略部署；推动文化产业高质量发展，推出更多同新时代相匹配的文化精品，实现从“高原”向“高峰”迈进；要深化供给侧结构性改革、体制机制改革；要加强党对宣传思想工作的全面领导，以党的政治建设为统领，牢固树立“四个意识”，坚决维护党中央权威和集中统一领导，牢牢把握正确政治方向；要增强能力本领，真正有“几把刷子”，不断增强脚力、眼力、脑力、笔力。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在深入学习领会全国宣传思想工作会议精神，系统分析中版集团成立16年来尤其是党的十八大以来的发展经验中，谭跃指出，“高质量发展”是集团落实全国宣传思想工作会议精神、开辟中版集团发展新时代的中心话题。在新的历史方位中，中版集团正迎来“高质量发展的新时代”。中版集团的高质量发展，包含着内容、市场、经济、产业、机制、党建、人才等多个维度，关联着数量、速度、结构、效益等多种指标，而其核心在于紧紧抓住坚持政治导向和出版导向、引领主流话语的内容高质量。集团要以“内容高质量”这个核心，以“定心做出版、关键数据化、有效国际化”为定力，履行建设“世界一流企业”的使命。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在交流环节，人民文学出版社、商务印书馆、中华书局等集团各单位主要负责人和总部本部各部门负责人，以中央会议精神为中心，围绕“落实意识形态工作责任制”“国际传播能力建设”“推动融合发展”“体制机制改革”“培育核心竞争力”“党的政治建设”6大重点话题，汇报出版思路和阶段性部署，推动集团整体工作迈上新台阶。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;明确中心话题，抓住高质量发展&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;记者从会上了解到，11月1日，中国出版集团结合“十三五”时期发展目标，尤其是结合“高质量发展”这一中心话题，下发了指导性的高质量发展指标体系，明确了出版高质量主要指标的基础目标数据、奋斗目标数据以及年均增幅等等。中版集团近年来始终按照“高质量发展”的总要求和“稳中求进”的基调，以供给侧结构性改革为主线，沿着“三六构想”“两调四强”“三型集团”次第推进的战略引领，以扎扎实实的硬功夫，不断推动集团发展质量变革、效率变革、动力变革。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;深入分析中国出版集团作为文化央企的特质，谭跃指出，中版集团要从政治的角度、意识形态的角度学习好、领会好习近平总书记重要讲话精神和中央精神，更要“重在实践”，把中央精神落实到集团的高质量发展上。他强调，高质量发展的根本前提和总要求是提高政治站位、履行政治使命、严守政治规矩、担当政治责任。一方面，高质量发展的出发点和落脚点都是发展。发展是质量的载体，是对质量的意义的揭示和价值的释放。有了发展才有质量问题，有了质量才能更好地发展，发展也才有了全新的阶段和意义；另一方面，高质量发展的关键是内容和经济的高质量发展。发展是主题，质量是关键。质量问题因发展的深化而来，发展阶段因质量的提高而提升。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;谭跃表示，中国出版集团成立16年来经历了从无到有、从小到大、从弱到强的发展过程。2012年，集团总资产迈过百亿大关，2016年，营收和净资产迈过百亿大关，进入全国为数不多的“三百亿”集团行列，整体经济实力得以较大增强，在全国保持了领先地位。2011~2017年，集团下属各单位依靠内容创新、做大主业、融合发展、资产经营、增强内功，取得了营业收入和利润双增长的突出成绩，也充分说明了“发展是硬道理”。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;针对内容的高质量，谭跃认为主要体现在选题具有政治的高度、社会的广度、思想的深度，凝结着政治性、思想性、大众性，体现了文化贡献和市场影响、社会效益和经济效益的有效结合和高度统一，最根本的前提是要坚持正确的政治方向和出版导向，认真履行意识形态责任制。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;坚持守正创新，推动“六个做优”&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;作为文化舆论的风向标，中国出版集团坚定不移地把导向管理放在工作的第一位，树立出版“国家队”责任意识，在行业内起到示范引领作用。具体工作落实上，集团各单位坚持“以人民为中心”的创作导向，坚持把社会效益放在首位，社会效益和经济效益相统一，配合党和国家的重要节点，做好主题出版工作；深化出版内容创新，加强传播方式创新，走出了一条具有自身特色的融合发展之路。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;谭跃指出，要深刻理解“守正创新”在中国出版集团工作实践中重要的指导意义。 所谓“守正”，就是要坚持正确导向第一、坚持国家使命第一、坚持文化影响第一，这是中版集团作为出版“国家队”的根本定位和总的方向。所谓“创新”，就是要坚持高质量发展，走集团特色的规模化、产业化、国际化发展之路。“守正创新”落到实处的关键，在于能否实现高质量发展；高质量是发展得好不好、竞争力强不强的关键所在。高质量要靠文化修养，靠历史和现代的视野，靠思想性和知识性，靠科技创新和机制创新，靠党的领导和党的建设，靠开拓进取的精神和求实务实的作风。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;就未来工作部署，谭跃提出要重点抓好“六个做优”，即做优内容生产和内容选择的高质量，做优内容传播和内容服务的高质量，做优体制机制和企业制度的高质量，做优融合发展和业态创新的高质量，做优传播中国文化和扩大国际交流的高质量，做优党的建设和作风建设的高质量。&lt;/span&gt;&lt;/p&gt;','中国出版集团',1,0,'/uploads/20181214/0a37b02f825228a2193fb9cd23379128.jpg',NULL,0,0,0,'2018-09-19 10:39:47','2018-12-14 12:23:49','2018-09-19 10:39:25'),(13,'黄坤明：着力打造有中国特色、有国际影响力的权威知识宝库','新华网','',1,'黄坤明：着力打造有中国特色、有国际影响力的权威知识宝库。','&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;中国大百科全书出版社成立40周年暨中国百科出版事业发展座谈会18日在京召开。中共中央政治局委员、中宣部部长黄坤明出席会议并讲话，强调要坚持以习近平新时代中国特色社会主义思想为指导，认真学习贯彻习近平总书记重要指示精神，继承发扬优良传统，自觉担当职责使命，努力将新版《中国大百科全书》打造成有中国特色、有国际影响力的权威知识宝库。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;黄坤明指出，《中国大百科全书》是伴随着改革开放而起步和发展起来的。40年来，中国大百科全书出版社汇聚科技界、学术界等各方面专家学者，先后编纂出版《中国大百科全书》第一版、第二版，建立起了富有中国特色的百科知识体系，为发展中国百科出版事业、繁荣社会主义文化作出了重要贡献。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;黄坤明指出，编纂新版《中国大百科全书》是一项国家基础性文化工程，党中央高度重视、寄予厚望。要深刻把握新时代新要求，坚持正确政治方向，坚定“四个自信”，坚守高端学术品位，积极创新编纂理念、出版手段、传播方式，组织动员各方力量统筹推进纸质版、网络版及外文版的编纂出版，努力以“十年磨一剑”精神铸造无愧于时代的传世精品，确保经得起读者检验、经得起历史检验。&lt;/span&gt;&lt;/p&gt;','新华网',1,0,'/uploads/20181214/cf0b281afc8de806dd4da5440768c2ff.jpg',NULL,0,0,0,'2018-09-19 10:41:04','2018-12-17 17:05:37','2018-09-19 10:40:09');

/*Table structure for table `os_printlinks` */

CREATE TABLE `os_printlinks` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '友情链接ID',
  `title` varchar(200) DEFAULT NULL COMMENT '友情链接标题',
  `url` varchar(200) DEFAULT NULL COMMENT '友情链接URL',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

/*Data for the table `os_printlinks` */

insert  into `os_printlinks`(`id`,`title`,`url`,`create_time`,`update_time`) values (1,'中国出版集团','http://www.cnpubg.com/',NULL,NULL),(2,'人名文学出版社','http://www.rw-cn.com/',NULL,NULL),(3,'北京大学出版社','http://www.rw-cn.com/',NULL,NULL);

/*Table structure for table `os_printnews` */

CREATE TABLE `os_printnews` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '时事新闻ID',
  `title` char(100) DEFAULT NULL COMMENT '标题',
  `source` varchar(200) DEFAULT NULL COMMENT '新闻来源',
  `url` varchar(200) DEFAULT NULL COMMENT '新闻链接',
  `cid` smallint(5) unsigned NOT NULL COMMENT '分类ID',
  `introduction` varchar(255) DEFAULT '' COMMENT '简介',
  `content` longtext COMMENT '内容',
  `author` varchar(20) DEFAULT '' COMMENT '作者',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '状态 0 待审核  1 审核',
  `reading` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '阅读量',
  `thumb` varchar(255) DEFAULT '' COMMENT '缩略图',
  `photo` text COMMENT '图集',
  `is_top` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否置顶  0 不置顶  1 置顶',
  `is_recommend` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否推荐  0 不推荐  1 推荐',
  `sort` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `publish_time` datetime DEFAULT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8 COMMENT='时事新闻表';

/*Data for the table `os_printnews` */

insert  into `os_printnews`(`id`,`title`,`source`,`url`,`cid`,`introduction`,`content`,`author`,`status`,`reading`,`thumb`,`photo`,`is_top`,`is_recommend`,`sort`,`create_time`,`update_time`,`publish_time`) values (3,'11月亚洲市场浆价走跌 短期可能仍向下滑落','纸业新闻','',4,'2018年12月14日纸厂废纸收购价格信息','&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;国际浆价在亚洲市场10月份维持平盘后，11月反转走跌。有市场人士预期短时间内浆价有可能受需求趋淡影响而下探，但长期来看，浆价仍呈上升走势。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;国际浆价自前年第4季起，开始一段涨幅高、涨速快且前所未有的长波段上升，甚至创下历史新高。今年上半年，国际浆价在各主要市场虽然呈现忽而走高忽而持平之局，但基本属于上升走势。不过，由于去年第4季至今年1月亚洲市场的浆价上涨幅度较大，故2、3月后则改由欧、美市场的浆价波动幅度较多。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;直至第3季初，国际浆价在亚洲市场大致仍维持平盘，8月份终于出现大幅回调，主要是NBSK报价下调，但很快地，9月份才回落的NBSK售价又反向走扬，而短纤报价（以BEK尤加利浆为主）则大致持平。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在欧美市场的第3季浆价走势，欧洲市场长短纤价格在各月也基本持平，但美国市场由于内需颇旺，因此长短纤价几乎是双双一路上行。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;第4季初的10月份，亚洲市场的浆价依然没有变动，其中加拿大拥有2条浆线年产能达60万吨的大厂Canfor，曾因锅炉损坏，于9月下旬起进行浆线的停机维修，时间长达70-80天，预估将减少NBSK产量约6-7万吨，因此曾宣布10月对亚洲市场调涨NBSK售价，每吨30美，但最终因下游没接受而未果。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;此外，欧洲市场也和亚洲市场同样呈现平盘。唯独美国市场的国际浆价仍持续上升，很显然该市场供求相当活跃，并不受中美贸易战影响。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;依据加拿大PPPC(Pulp&amp;amp;PaperProductsCouncil)的最新报告显示，国际市场浆9月份的总装船量为462.4万吨，较上月增加4.4%，较去年同期增加7.1%。其中，销至北美市场估计为64.2万吨，较上月增加3.9%，而较去年同期减少6%。而装船至西欧市场估计为132.6万吨，较上月增加7.9%，且较去年同期增加8.5%。另输至中国大陆市场为149万吨，较上月增加23.3%，且较去年同期增加16.7%。而以上装船量相对于产量之比为97%，较上月再提升11%。由上显示，9月份的国际市场浆需求颇旺。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在库存量方面，9月底的市场浆库存为37天，较上月减少1天。其中漂白长纤木浆维持和上月相同的33天，漂白短纤木浆则为41天，较上月减少2天。若从长纤库存平衡天数28-30天，短纤39天来看，9月份的市场浆库存略为超过正常存量。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;至于11月份的国际浆价走势，亚洲市场和欧美市场依然出现背离情形。亚洲市场在力撑平盘一段时间后，无论长短纤都呈现全面反向走跌。其中，放射松每吨降60美，由上月的880美降为820美，长纤浆则下调30美，约由每吨890美降至860美，而据悉，俄罗斯的NBKP到中国大陆现货价已降至800美，短纤浆降幅最小，每吨下跌10美，新价约为780美（最高则有805美，依个案条件不同而有别）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;据业界观察，亚洲市场浆价全面反转下跌，主要受中国大陆市场需求转冷以及中美贸易战的影响，加上其贸易进口商纸浆库存尚处于高水位所致。依据中国造纸协会统计，今年前3季中国大陆各类纸张（纸与纸板）生产量、销售额、利润均比去年同期明显下降。原因主要是造纸业生产成本增加至少10%以上，但纸价起落无常，近期则出现明显跌幅，加上环保因素影响，不少纸厂的生产线实施短暂停产计划，故而影响营收与获利，尤其工业纸厂受禁废令波及，导致成本明显上升，间接影响获益。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;而在节节高升的整体生产成本中，浆价上扬成本即占25%，加上人民币汇率大幅贬值约1成，又抬高了纸浆进口成本，另一方面需求减少，都使得中国大陆市场浆价守稳不易。今年以来中国大陆纸浆进口贸易商不断抢进国际市场浆，不仅使得纸厂减少本地纸浆用量，结果形成受需求降低影响，又对进口纸浆难以消化，导致进口浆的库存大增。目前这些库存尚待去化，自然会影响后续的量、价走势。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;市场人士预测，短期内以中国大陆为主的亚洲浆价可能会是向下走势。而这是否意味持续将近2年的浆价上升波段将暂告一段落，尚须进一步观察。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;不过，在欧美市场，浆价持续呈现上涨格局。欧洲市场NBSK每吨调升20-30美，新价达1250美之上，短纤浆则维持平盘。美国市场，无论长短纤价格调幅皆为20-30美，其中NBSK提高30美，达1460美，而BEK则涨20美，新价涨至1290美。但有业者认为，短期内，受中国大陆市场浆价下跌影响，欧美市场浆价有可能会跟着向下调整，实际走势，亦值得关注。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;业界分析，国际短纤浆到2020年前并无新产能投市，而长纤浆近年将有芬兰的一家新增80万吨的商品浆产能，故估计短期内，短纤浆价会比长纤浆价持稳。据悉，全球纸浆总供应量的增长率低于总需求量的增长，而大型浆厂新增产能须到2020年后才会问世，因此预期在2020年前，国际浆价仍将呈上升走势。&lt;/span&gt;&lt;/p&gt;','纸业新闻',1,0,'/uploads/20181214/d4b8b22c0931429665f7f4d781e21e13.jpg',NULL,0,0,0,'2018-09-11 15:17:57','2018-12-14 11:19:19','2018-09-11 15:16:01'),(4,'2018年新闻出版行业现状分析：传统出版物出版量连年下滑，数字出版年年走高','前瞻产业研究院','',4,'2018年新闻出版行业现状分析','&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;新闻出版业规模持续扩大，效益稳步提升&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;近年来，在供给侧改革推动下，我国新闻出版业实现良好发展，行业规模持续扩大，行业效益稳步提升。2017年我国新闻出版业（出版、印刷和发行服务&amp;lt;不含数字出版&amp;gt;）实现营业收入18119.2亿元，较2016年同比增长4.5%；拥有资产总额22165.4亿元，同比增长3.0%；利润总额1344.3亿元，同比增长2.7%。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;其中，图书出版2017年取得了879.6亿元的营收，占比最重，达52.26%，为新闻出版业的最主要收入来源；其次为报纸出版，营收占比34.48%；期刊出版与音像制品出版分别占比11.64%和1.72%。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;出版物纯销售数量、金额双双创历史新高&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2017年，我国书业（全国新华书店系统、出版社自办发行单位）出版物纯销售72.80亿册（张、份、盒），销售额达909.35亿元，销售数量同比增长3.63%，销售金额同比增长6.67%，把书业推向新高。这是在2016年书业出版物纯销量首跃800亿元新阶后，一年间的再跃新阶。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;近几年来，出版物收到数字出版、移动阅读等新兴出版阅读的双重挑战，实体书单一度式微。2017年出版物纯销售数量、金额的双增，体现书实体书店的回暖和振兴，更体现出出版业对全民阅读的强力推进，反映了出版物销售的大势。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;同时，2017年出版物纯销售的双增还使全国人均购书数量和购书金额提高。按国家统计局公布的2017年年末全国人口计算，2017年全国人均购书5.24册，65.42元。5.24册的人均购书，不仅同比增长3.15%，还创下自2003年以后14年间全国人均购书量的新高。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;传统出版物出版量连年下滑，数字出版年年走高&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2017年全国图书、期刊、报纸、音像制品和电子出版物出版的总量为485.23亿册（份、盒、张），同比降低5.43%；其中图书、期刊、报纸总印张为2020.94亿印张，同比降低7.99%。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;具体来看，期刊出版总印数24.92亿册，同比降低7.59%；报纸出版总印数362.50亿份，同比降低7.07%；音像制品出版2.56亿盒（张），同比降低7.22%；电子出版物出版2.81亿张，同比降低3.21%。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;与上述传统出版物销售情况相对的，是数字出版销售额的连年走高。2013-2017年，数字出版保持着双位数的增长，营业收入连年增长，从2013年的2540.4万元的营业额增长至2017年的7071.9亿元，年均复合增长率达到29.17%。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;事实上，在互联网大数据时代，由于网络阅读、移动阅读的兴起、兴盛，传统纸介质出版物衰弱，新兴出版蓬勃发展是大势所趋。但并不代表以报纸、期刊为代表的纸质载体读物会完全消亡。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;其未来生存发展需要两方面的合力：一是主管部门加强分类指导和对品牌党报党刊、学术报刊、行业报刊等的扶持力度，促其融合发展；二是出版业应通过供给侧结构性改革，顺势落实去产能，该关停并转就关停并转，促进资源整合、存量盘活、新量逆增；报刊自身应巩固其内容创造的内核，加大数字化转型力度，切实践行融合发展。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;出版业应融合发展&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;从整体行业来讲，出版物销售要实现持续增长，要强化三个融合：一是供应机构的融合，即书店与图书馆深度协作、融合，打造“你中有我、我中有你”的供应链；二是销售方式的融合，即卖场销售和网络销售的融合，线上线下互补，O2O模式互动；三是传播手段的融合，即传统营销传播和互联网、移动互联网营销传播融合，微博、微信、APP互动，立体传播分享。&lt;/span&gt;&lt;/p&gt;','万亚男',1,0,'/uploads/20181214/28c4aa5cc7d519f26af33b75d727ad30.jpg',NULL,0,0,0,'2018-09-11 17:09:44','2018-12-14 12:16:20','2018-09-11 17:09:00'),(5,'包装设备行业发展迅猛 专业化水平不断提升','中国纸业','',4,'北京中版联印刷有限公司（简称中版联公司）','&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;中国包装设备起步较晚，1980年尚未形成工业体系，改革开放后，社会对包装机械的需求不断增加，加上政府的重视与扶持，中国包装机械得到迅速发展，年平均增长速度大于30%。进入20世纪90年代后，其仍以20%以上的速度增长。经过20多年的发展，中国包装机械已成为机械工业中十大行业之一。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;行业经营效益趋于改善&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;随着包装逐渐演变成一种营销工具以及消费者对包装便捷性和产品信息需求的不断提高，包装机械市场也取得了长足的发展。2010-2017年，我国包装专用设备主营业务收入整体上呈现增长趋势，但是增长速度波动幅度较大，2017年我国包装专用设备主营业务收入为325.1亿，同比增长4.89%。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;与包装专用设备规模以上主营业收入一样，近年来，我国包装专用设备的利润总额也呈现不稳定性，但是总体上趋于发展利好，从2010年的13.21亿增加至2017年17.93亿，共增加了4.72亿。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;随着包装市场需求的蓬勃发展，在技术水平增强、进口替代效应逐步显现等因素的推动下，我国包装机械行业也迎来了良好的发展时期。2014-2017年我国的包装专用设备产量处于较平稳状态，均维持在10台左右，截止2018年7月，我国包装专用设备产量达到51515台，同比下降6.8%。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;浙江产量最大河北增长最快&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;从2018年1月-7月全国包装专用设备主要地区产量来看，除江苏、吉林和河北实现同比增长外，其余地区均实现明显下降趋势。前5省市分别是浙江省、北京市、湖北省、上海市、广东省，但是其中浙江省完成累计产量9984台，同比下降21.81%；北京完成累计产量8230台，同比下降3.14%；湖北完成累计产量7306台，同比下降2.79%；上海完成累计产量5090台，同比下降6.07%；广东省完成累计产量4922台，同比下降39.93%。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;包装专业设备发展趋势&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;根据行业分析师分析，近几年来，中国包装机械业取得了快速发展，但也出现了一些低水平重复建设现象。据有关资料显示，与工业发达国家相比，中国食品和包装机械产品品种缺乏25%-30%，技术水平落后15-25年。今后中国包装机械市场发展将呈现以下发展趋势：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;趋势一：包装机械技术含量日趋增加&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;中国现有的一些包装机械产品技术含量不高，而国外已将很多先进技术应用在包装机械上，如远距离遥控技术(包括监控)、步进电机技术、自动柔性补偿技术、激光切割技术、信息处理技术等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;趋势二：包装机械市场日趋垄断化&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;目前，中国除了瓦楞纸箱包装机械和一些小型包装机械有一定规模和优势外，其他包装机械几乎不成体系和规模，特别是市场上需求量大的一些成套包装生产线，在世界包装市场中均被几家大包装机械企业(集团)所垄断。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;趋势三：包装机械零部件生产专业化&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;国际包装界十分重视提高包装机械加工和整个包装系统的通用能力，所以包装机械零部件生产专业化是发展的必然趋势，很多零部件不再由包装机械厂生产，而是由一些通用的标准件厂生产，某些特殊的零部件由高度专业化的生产厂家生产，真正有名的包装机械厂将可能是组装厂。产品向多功能与单一、高速两极化发展。包装机械的最终作用在于提高生产效率和产品多样化。&lt;/span&gt;&lt;/p&gt;','前瞻产业研究院',1,0,'/uploads/20181214/480bacdd55063ebaabd61659ce82c3b5.jpg',NULL,0,0,0,'2018-09-11 17:10:47','2018-12-14 12:16:15','2018-09-11 17:09:52'),(6,'在开放中升华改革气质','人民论坛','',1,'在开放中升华改革气质','&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 2em;&quot;&gt;40载岁月激扬，持续扩大对外开放、主动拥抱世界的中国，为改革创新提供了沃土。从深圳等沿海经济特区的腾飞，到一批内陆开放城市逐渐崛起；从加入世界贸易组织，到深度融入全球经济体系；从加快自贸区、自贸港建设，到修订负面清单、大幅放宽外资准入限制……回溯既往，如果没有对外开放，很多改革实践就不可能“迈开步子”，更不可能“跑步前行”。以开放促改革、促发展，是改革开放40年来的一条宝贵经验。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;开放联通世界，如同加速改革的催化剂。不久前，世界银行一份营商环境报告显示，中国排名比上年度跃升32名，成为东亚及太平洋地区唯一进入该报告十大最佳改革者名单的经济体。多年来，世界银行的这份榜单一直是跨国企业投资决策的重要参考，中国许多城市就以这份榜单背后的评价体系为标准，不断深化改革，着力改善营商环境。通过引入国际标准、对接全球市场，中国改革者学会了变压力为动力、化危机为契机。可以说，对外开放不仅打开了一扇窗口，更把竞争对手放进来、把先进理念和技术引进来，从而极大推动了改革、鼓励了创新。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;开放是压力，带来动力；开放也是信念，激励担当。有学者将中国对外开放的历程划分为3个阶段：上世纪80年代主要是“请进来”，到90年代主要是“接轨”，到本世纪初则开始“走出去”。这样的转变意味着，今天的开放已经不仅仅是要为发展创造外部环境和条件，开放本身业已融入中国改革者的视野和意识之中，成为指导行动的价值理念。从提出“一带一路”倡议，到推动构建人类命运共同体；从发起建立亚洲基础设施投资银行，到举办世界首个国家级进口博览会……中国对外开放的一系列主动探索、主动作为，推动了全球治理体系变革，也促进了国内各领域的深化改革。今日之中国，以更积极的姿态活跃在国际舞台，也为自身的改革发展创造了广阔空间。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;10余年前，《世界是平的》一书形象描绘了经济全球化的景象。作者认为，个人电脑的普及、互联网的繁荣、离岸经营、全球供应链等，将把世界“抹平”。然而，这一乐观的预言，如今却日益面临单边主义、贸易保护主义、逆全球化思潮等挑战。封闭没有出路，开放才有未来。中国的改革实践有力证明：发展中遇到的问题，惟有通过开放、合作才能解决。“关起门来过日子”的想法和行为，不仅是片面的、守旧的，也终将错过开放发展的红利。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;改革开放之初，一位中国企业家专程到美国福特汽车博物馆参观。他想看看中国当时的工业水平处于美国工业史上的哪个阶段，以便为自己的企业找寻方向。40年过去，中国开始在一些领域逐渐步入创新的“无人区”；我们的未来，早已不会藏在别人的博物馆里。当中国开放的大门越开越大，新时代中国人必将在开放中不断升华改革气质，续写新时代改革开放的新史诗。&lt;/span&gt;&lt;/p&gt;','彭飞',1,0,'/uploads/20181214/d58bf915427ffc0389356b8bcf951202.jpg',NULL,0,0,0,'2018-09-14 08:45:10','2018-12-14 12:15:53','2018-09-14 08:44:30'),(7,'分析研究2019年经济工作，研究部署党风廉政建设和反腐败工作','人民日报','',1,'分析研究2019年经济工作，研究部署党风廉政建设和反腐败工作。','&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;新华社北京12月13日电 中共中央政治局12月13日召开会议，分析研究2019年经济工作；听取中央纪律检查委员会工作汇报，研究部署2019年党风廉政建设和反腐败工作。中共中央总书记习近平主持会议。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;会议认为，今年以来，面对错综复杂的国际环境和艰巨繁重的国内改革发展稳定任务，我们按照党的十九大作出的战略部署，坚持稳中求进工作总基调，落实高质量发展要求，有效应对外部环境深刻变化，迎难而上、扎实工作，改革开放继续深化，各项宏观调控目标可以较好完成，三大攻坚战开局良好，供给侧结构性改革深入推进，人民群众得到更多实惠，保持了经济持续健康发展和社会大局稳定，朝着实现第一个百年奋斗目标迈出新的步伐。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;会议指出，2019年是新中国成立70周年，是决胜全面建成小康社会第一个百年奋斗目标的关键之年。做好明年经济工作，要以习近平新时代中国特色社会主义思想为指导，统筹推进“五位一体”总体布局，协调推进“四个全面”战略布局，坚持稳中求进工作总基调，坚持新发展理念，坚持推进高质量发展，坚持以供给侧结构性改革为主线，坚持深化市场化改革、扩大高水平开放，加快建设现代化经济体系，继续打好三大攻坚战，着力激发微观主体活力，创新和完善宏观调控，统筹推进稳增长、促改革、调结构、惠民生、防风险工作，保持经济运行在合理区间，进一步稳就业、稳金融、稳外贸、稳外资、稳投资、稳预期，提振市场信心，提高人民群众获得感、幸福感、安全感，保持经济持续健康发展和社会大局稳定，为全面建成小康社会收官打下决定性基础，以优异成绩庆祝中华人民共和国成立70周年。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;会议强调，要辩证看待国际环境和国内条件的变化，增强忧患意识，继续抓住并用好我国发展的重要战略机遇期，坚定信心，把握主动，坚定不移办好自己的事。要保持战略定力，注重稳扎稳打，加强协调配合，聚焦主要矛盾，把握好节奏和力度，努力实现最优政策组合和最大整体效果。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;会议指出，明年要继续打好三大攻坚战，按照已确定的行动方案，针对突出问题，打好重点战役。推动制造业高质量发展，推进先进制造业与现代服务业深度融合。促进形成强大国内市场，提升国民经济整体性水平。扎实推进乡村振兴战略，巩固发展“三农”持续向好形势。促进区域协调发展，发挥好各地区比较优势。加快经济体制改革，推动全方位对外开放。加强保障和改善民生，着力解决好人民群众反映强烈的突出问题。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;会议强调，做好经济工作，必须提高党领导经济工作的能力和水平，加强党中央集中统一领导，激励干部担当作为，加强学习和调查研究，创造性贯彻落实党中央方针政策和工作部署。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;会议要求，各地区各部门要做好岁末年初的民生保障、安全生产、社会稳定等各项工作，确保明年工作开好局、起好步。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;会议指出，党的十九大以来，以习近平同志为核心的党中央一以贯之、坚定不移推进全面从严治党，党内政治生态展现新气象，反腐败斗争取得压倒性胜利，全面从严治党取得重大成果。中央纪委始终自觉同党中央保持高度一致，坚持以习近平新时代中国特色社会主义思想为指导，贯彻落实党的十九大全面从严治党战略部署，深化纪检监察体制改革，推动构建党统一指挥、全面覆盖、权威高效的监督体系。坚持发现问题和整改落实并重，巩固深化巡视巡察工作。巩固拓展作风建设成果，坚定不移纠“四风”、树新风。全面加强党的纪律建设，切实履行监督第一职责。深化标本兼治，保持惩治腐败的高压震慑。坚决整治群众身边腐败和作风问题。加强纪检监察队伍建设，各项工作开创新局面。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;会议强调，反腐败斗争形势依然严峻复杂，全面从严治党依然任重道远，必须将“严”字长期坚持下去，不断推进党的自我革命，实现自我净化、自我完善、自我革新、自我提高。要坚持用习近平新时代中国特色社会主义思想武装头脑，深入贯彻党的十九大精神，以党的政治建设为统领，持之以恒落实中央八项规定精神，紧盯不敬畏、不在乎、喊口号、装样子的问题，坚决破除形式主义、官僚主义，推动党中央重大决策部署落地见效。要创新纪检监察体制机制，做实做细监督职责，深化政治巡视，完善巡视巡察战略格局，着力在日常监督、长期监督上探索创新、实现突破。要有力削减存量、有效遏制增量，巩固发展反腐败斗争压倒性胜利，整治群众身边腐败和作风问题。要时刻铭记打铁必须自身硬的要求，从严从实加强纪检监察队伍建设，努力做到政治过硬、本领高强，打造忠诚干净担当的纪检监察铁军。此前，习近平总书记主持召开中央政治局常委会会议，听取中央纪委2018年工作情况和十九届中央纪律检查委员会第三次全体会议准备情况汇报。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;会议同意明年1月11日至13日召开十九届中央纪律检查委员会第三次全体会议。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;《 人民日报 》（ 2018年12月14日 01 版）&lt;/span&gt;&lt;/p&gt;','人民网',1,0,'/uploads/20181214/e2feeb03b3aef3e95eb7d2b8b012b20d.jpg',NULL,0,0,0,'2018-09-14 08:49:19','2018-12-14 12:15:46','2018-09-14 08:47:54'),(8,'习近平的2018：11月关键词——开放型世界经济','新华网','',2,'习近平的2018','&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;【学习进行时】行程6万公里，出席百余场活动，在习近平总书记11月的工作安排中，分量最重的无疑就是外交。在一系列外交场合，习近平谈得最多的话题之一就是构建开放型世界经济。新华社《学习进行时》原创品牌栏目“讲习所”今天推出文章，以“开放型世界经济”为关键词，为您梳理习近平11月的重要活动。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;在习近平总书记11月的工作安排中，分量最重的无疑就是外交。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;上旬，首届中国国际进口博览会举行，习近平出席开幕式发表主旨演讲，还同与会的有关国家领导人举行了一系列双边会见。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;中旬，他又远渡重洋，前往巴布亚新几内亚出席亚太经合组织第二十六次领导人非正式会议，并访问巴新、文莱、菲律宾。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;11月27日至12月5日，习近平再次踏出国门，出席在布宜诺斯艾利斯举行的二十国集团领导人峰会并访问了西班牙、阿根廷、巴拿马、葡萄牙。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;这3次重要外交活动中，习近平谈得最多的话题之一就是构建开放型世界经济。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;首届中国国际进口博览会——传递强烈信号&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;经过一年多筹备，在各方大力支持下，首届中国国际进口博览会11月5日正式开幕，习近平主席出席开幕式并发表主旨演讲。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;中国国际进口博览会，是迄今为止世界上第一个以进口为主题的国家级展会，是国际贸易发展史上一大创举。习近平指出，这体现了中国支持多边贸易体制、推动发展自由贸易的一贯立场，是中国推动建设开放型世界经济、支持经济全球化的实际行动。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;虽然世界经济深刻调整，保护主义、单边主义抬头，经济全球化遭遇波折，多边主义和自由贸易体制受到冲击，但着眼现实、放眼未来，经济全球化是不可逆转的历史大势、建设开放型世界经济的必然要求。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;细读习近平在进博会开幕式上的主旨演讲，“建设开放型世界经济”是统领全篇、贯穿始终的关键词。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;为推动开放合作，习近平提出3点主张：&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——各国应该坚持开放融通，拓展互利合作空间；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——各国应该坚持创新引领，加快新旧动能转换；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——各国应该坚持包容普惠，推动各国共同发展。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;归根结底，人类社会要持续进步，各国要开放不要封闭，要合作不要对抗，要共赢不要独占。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;作为世界第二大经济体，中国对全球经济发展举足轻重。对于中国经济的前景，习近平满怀信心。他郑重宣布中国为进一步扩大开放将实施的5方面举措：&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——激发进口潜力；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——持续放宽市场准入；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——营造国际一流营商环境；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——打造对外开放新高地；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——推动多边和双边合作深入发展。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;3点主张和5方面举措向世界传递了极为强烈的信号：中国推动更高水平开放的脚步不会停滞！中国推动建设开放型世界经济的脚步不会停滞！中国推动构建人类命运共同体的脚步不会停滞！&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;亚太经合组织领导人非正式会议——两提“十字路口”&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;在巴新出席亚太经合组织第二十六次领导人非正式会议期间，习近平发表了两篇十分重要的讲话。两篇讲话都提到了一个词——“十字路口”，颇具深意。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;11月17日出席亚太经合组织工商领导人峰会时，习近平强调，当今世界的变局百年未有，变革会催生新的机遇，但变革过程往往充满着风险挑战，人类又一次站在了十字路口。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;18日出席亚太经合组织第二十六次领导人非正式会议时，习近平强调，站在历史前进的十字路口，我们应该认清世界大势，把握经济脉动，明确未来方向，解答时代命题。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;站在历史前进的十字路口，“智者顺时而谋。”所谓“顺时”，就是把握历史前进的规律。习近平再次回顾历史，特别是近代以来亚太地区的历史，告诉人们，经济全球化是人类社会发展必经之路，多边贸易体制为各国带来了共同机遇。因此，面对历史大潮，只有构建开放型世界经济才是正确的方向。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;两次讲话，习近平从推动建设开放型世界经济出发，给出了大力倡导开放合作的中国方案。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;在工商领导人峰会上，习近平提出了5个“导向”：&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持开放导向，拓展发展空间；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持发展导向，增进人民福祉；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持包容导向，促进交融互鉴；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持创新导向，开辟增长源泉；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持规则导向，完善全球治理。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;在领导人非正式会议上，习近平提出4点“坚持”：&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持推进区域经济一体化，构建开放型亚太经济；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持创新驱动，培育增长新动能；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持完善互联互通网络，促进包容联动发展；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持深化伙伴关系，携手应对共同挑战。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;在具体阐释中，习近平多次提到持续推进贸易和投资自由化便利化，提到开放、包容、普惠、平衡、共赢的方向。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;正如习近平指出的，我们应该顺应经济全球化发展大势，不为一时的困难所阻挡，继续秉持推动区域经济一体化宗旨，把握构建开放型世界经济大方向，努力保持亚太合作势头，稳步迈向更高水平。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;二十国集团领导人峰会——不断发出时代强音&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;今年是国际金融危机发生10周年，也是二十国集团领导人峰会10周年。11月30日，习近平在布宜诺斯艾利斯出席二十国集团领导人峰会时指出，10年后，我们应该再次拿出勇气，展示战略视野，引领世界经济沿着正确轨道向前发展。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;这个正确轨道，无疑就是坚定维护经济全球化和多边贸易体制，建设开放型世界经济。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;习近平说，5年前，我第一次出席二十国集团领导人峰会，呼吁共同维护和建设开放型世界经济。现在看，这一任务更加迫切。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;他提出4个“坚持”：&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持开放合作，维护多边贸易体制；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持伙伴精神，加强宏观政策协调；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持创新引领，挖掘经济增长动力；&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;——坚持普惠共赢，促进全球包容发展。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;4个“坚持”反映出一个深刻的道理，无论前途是晴是雨，携手合作、互利共赢是唯一正确选择。这既是经济规律使然，也符合人类社会发展的历史逻辑。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;此次出访期间，习近平反复强调促进开放型世界经济，旗帜鲜明反对保护主义、单边主义。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;11月30日，金砖国家领导人非正式会晤时，习近平指出，我们要坚定支持多边主义，共同维护多边主义和以规则为基础的国际秩序，反对保护主义、单边主义，推动国际社会合作应对重大挑战。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;中俄印领导人非正式会晤时，习近平强调，中俄印要推动贸易投资自由化、便利化，促进开放型世界经济，旗帜鲜明反对保护主义、单边主义，共同捍卫多边贸易体制，维护新兴经济体和发展中国家利益。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;会见联合国秘书长古特雷斯时，习近平强调，我提出构建人类命运共同体，倡议共建“一带一路”，就是在反复思考世界各国应如何在千差万别的利益和诉求中实现共商共享、和而不同、合作共赢。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;……&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;在各种场合，中国领导人不断发出开放合作的时代强音，为建设开放型世界经济注入正能量。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;多次国内活动——“改革开放”是高频词&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;中国始终致力于推动经济全球化，建设开放型世界经济。改革开放40年的历史就是最好的证明。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;11月，习近平的国内重要活动一如既往地频繁，“改革开放”是他在这些活动中的高频词。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;改革开放40年来，民营经济已经成为推动我国发展不可或缺的力量。11月1日，习近平主持召开民营企业座谈会并发表重要讲话。他指出，在我国经济发展进程中，要不断为民营经济营造更好发展环境，帮助民营经济解决发展中的困难，变压力为动力，让民营经济创新源泉充分涌流，让民营经济创造活力充分迸发。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;这次座谈会上，习近平提出减轻企业税费负担、解决民营企业融资难融资贵问题、营造公平竞争环境、完善政策执行方式、构建亲清新型政商关系、保护企业家人身和财产安全等6个方面政策举措落实，让民营企业和民营企业家吃下定心丸、安心谋发展。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;出席首届中国国际进口博览会开幕式后，11月6日至7日，习近平又深入上海的企业、社区、城市运行综合管理中心、高新科技园区进行调研。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;上海是全国改革开放排头兵，在全国发展的大布局中，有着“先手棋”的地位。考察期间，习近平对上海提出了5个方面的工作要求，包括更好为全国改革发展大局服务、推动经济高质量发展、推动改革开放向纵深发展、深化社会治理创新、提高党的建设质量和水平。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;他希望上海勇于挑最重的担子、啃最难啃的骨头，发挥开路先锋、示范引领、突破攻坚的作用，为全国改革发展作出更大贡献。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;white-space: normal; text-align: justify; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;11月，习近平还会见了香港澳门各界庆祝国家改革开放40周年访问团、参观庆祝改革开放40周年大型展览、主持召开中央深改委第五次会议等，对庆祝改革开放40周年、不断把新时代改革开放继续推向前进提出一系列重要要求。&lt;/span&gt;&lt;/p&gt;','新华网记者：王子晖',1,0,'/uploads/20181213/398818faaec0a1256090f7090cf732da.jpg',NULL,0,0,0,'2018-09-19 15:48:28','2018-12-18 14:12:52','2018-09-19 15:46:50');

/*Table structure for table `os_printpaper` */

CREATE TABLE `os_printpaper` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '纸质文化表ID',
  `title` varchar(100) DEFAULT NULL COMMENT '纸质文化表标题',
  `source` varchar(200) DEFAULT NULL COMMENT '纸质文化来源',
  `url` varchar(200) DEFAULT NULL COMMENT '纸质文化表url',
  `cid` smallint(5) unsigned NOT NULL COMMENT '分类ID',
  `introduction` varchar(255) DEFAULT '' COMMENT '简介',
  `content` longtext COMMENT '内容',
  `author` varchar(20) DEFAULT '' COMMENT '作者',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '状态 0 待审核  1 审核',
  `reading` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '阅读量',
  `thumb` varchar(255) DEFAULT '' COMMENT '缩略图',
  `photo` text COMMENT '图集',
  `is_top` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否置顶  0 不置顶  1 置顶',
  `is_recommend` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否推荐  0 不推荐  1 推荐',
  `sort` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `publish_time` datetime DEFAULT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8 COMMENT='纸质文化表';

/*Data for the table `os_printpaper` */

insert  into `os_printpaper`(`id`,`title`,`source`,`url`,`cid`,`introduction`,`content`,`author`,`status`,`reading`,`thumb`,`photo`,`is_top`,`is_recommend`,`sort`,`create_time`,`update_time`,`publish_time`) values (8,'胶版纸',NULL,'',1,'胶版纸的文化简介','&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;简介&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;旧称&amp;quot;道林纸&amp;quot;，主要供平版（胶印）印刷机或其他印刷机印刷较高级彩色印刷品时使用，适于印制单色或多色的书刊封面、正文、插页、画报、地图、宣传画、彩色商标和各种包装品。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;胶版纸伸缩性小，对油墨的吸收性均匀、平滑度好，质地紧密不透明，白度好，抗水性能强，应选用结膜型胶印油墨和质量较好的铅印油墨。油墨的粘度也不宜过高，否则会出现脱粉、拉毛现象。还要防止背面粘脏，一般采用防脏剂、喷粉或夹衬纸。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;胶板纸按纸浆料的配比分为特号、1号和2号、3号，具有较高的强度和适印性能。有单面和双面之分，还有超级压光与普通压光两个等级。胶版印刷是比较高级的书刊印刷纸，对比度、伸缩率和表面强度有较高的要求，酸碱性也应接近中性或呈弱碱性，以免影响印刷用的纸张。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;胶板纸伸缩性小，对油墨的吸收性均匀、平滑度好，质地紧密不透明，白度好，抗水性能强，应选用结膜型胶印油墨和质量较好的铅印油墨。油墨的粘度也不宜过高，否则会出现脱粉、拉毛现象。还要防止背面粘脏，一般采用防脏剂、喷粉或夹衬纸。重量：50，60，70，80，90，100，120，150，180（g/m2）&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;平板纸规格：787×1092，850×1168，880×1230&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;卷筒纸规格：宽度787，1092，850&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong style=&quot;text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;胶板纸特性&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;胶板纸及使用。胶板纸也称胶板印刷纸，是一种较为高档的印刷纸，一般专供胶印机作书版或彩色版印刷。胶板纸分单面胶板纸和双面胶板纸，又有超级压光、普通压光之分 。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;（1）胶板纸的原料与胶板纸的性能。胶版纸一般采用漂白针叶木化学浆和适量的竹浆制成。加工胶版纸时，加填、施胶较重，一些高档胶版纸，还要进行表面施胶、压光。胶版纸在印刷时由于采用的是水墨平衡的原理，因此需要纸张有很好的抗水性、尺寸稳定性和强度。胶版纸多用于彩色印刷品，为使油墨能够复原出原稿的色调，要求具有一定的白度及光滑度。胶版纸一般用于印刷画册、彩色插图、商标、封面、环衬、高档书籍和本册等。近些年由于人们文化、生活水平的提高，书籍材料也向高档次发展，使用胶版纸印刷书刊、本册等越来越多。由于胶版纸有质白、挺括1平坦细腻等长处，书刊本册制成后文字清楚、书刊平坦不易变形，与秃版、新闻纸书刊相比，档次格外不同，深得读者和使用者的欢迎。用胶版纸装订书刊本册的数量，今后还会大量增加，以应市场需要。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;（2）胶版纸的规格。胶版纸的规格与上两种纸相同。其它规格可参观国标GB147《印刷、书写及绘图用原纸尺寸》，尺寸误差、偏斜度不许超过±3mm。胶版纸的定量有多种，一般常用定量为60、70、80、90、120、150、180、200、250g/m2。印书刊的胶版纸均在90g/m2以下，封面用越为 70－200g/m2之间。胶版纸具有挺括、平滑等特点，易装订加工。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;①撞页时采用任何方法都能达到良好的撞页整洁效果。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;②任何折页机均可取得良好的折页效果，特殊相宜高速折页。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;③胶版纸书刊本册，设计时应以叁折页以下为主（决不可设计四折页）。最好是二折（80g/m2以上的）后再套帖，因为胶版纸厚、定量大，折数过多书帖内易出八字形皱折或不易伸展开的死折，造成切书后书册切口不齐、书芯不平等质量问题。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;④铁丝订书时与秃版纸页数或厚度相同时，要中大铁丝的直径。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;⑤粘结剂的粘度要比用于秃版纸的大（稠些），因为胶版纸定量大、表面光滑，粘度低了（稀）粘不牢或粘后脱落。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;⑥胶版纸的精装书芯不易扒圆、起脊，特殊是帖较厚时。因为胶版纸滑，扒圆后易使每一书帖暴露而前口圆势出现梯外形；起脊时也因手工的敲咂而使折缝内皱折无法舒平而造成次品。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;⑦胶版纸制作书刊，一般档次较高，因此封面材料应优于书芯材料，使书籍本册内外的相匹配。&lt;/span&gt;&lt;/p&gt;','admin',1,0,'/uploads/20180913/9a3eb8ee450808b526d4e6558435aa9d.jpg',NULL,0,0,0,'2018-09-13 10:25:16','2018-12-18 12:15:36','2018-09-13 10:20:47'),(9,'铜版纸',NULL,'',1,'铜版纸的文化简介','&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;简介&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;铜版纸又称涂布印刷纸，在香港等地区称为粉纸。它是以原纸涂布白色涂料制成的高级印刷纸。主要用于印刷高级书刊的封面和插图、彩色画片、各种精美的商品广告、样本、商品包装、商标等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;铜版纸为平板纸，尺寸为787×1092mm，880×1230mm，定量为70～250g/平方米。铜版纸有单面铜版纸、双面铜版纸、无光泽铜版纸、布纹铜版纸之分。根据质量分为A、B、C 三等。铜版纸的主要原料是铜版原纸和涂料。对铜版原纸的要求是厚薄均匀，伸缩性小，强度较高 ，抗水性好。纸面不许有斑点、皱纹、孔眼等纸病，用来涂布的涂料是由优质的白色颜料、胶粘剂（如聚乙烯醇、干酪素等）及辅助添加剂等组成的。将这种流动性大且固体物含量高的涂料，通过涂布机薄而均匀地涂刷在原纸上，然后进行干燥，在卷纸机上卷成卷筒状，再送到超级压光机上进行压光整饰，最后分切、选纸、打包。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong style=&quot;text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;历史由来&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;铜版纸是大陆的叫法，香港称之为粉纸，是印刷业中使用较多的纸张之一。在香港对于这个纸名，几乎是众所周知。但在内地却知者不多，因为从业者互相间缺乏沟通。粉纸的英文原名是Art Paper，它是一个舶来品的俗名。这种纸是在19世纪中叶，由英国人首先研制出来的一种涂布加工纸。把又白又细的瓷土等调和成涂料，均匀地刷抹在原纸的表面上（涂一面或双面），便制成了高级的印刷纸。由于其过程好比妇女向脸面上涂粉似的，因此便称呼为粉纸。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;其实，Art Paper在中国三十年代曾译作美术纸（直译）。因为当初在欧洲拿这种纸来印刷精美的名画时，晒制所用的是铜版腐蚀的印版。所以依据以用途命名的惯例，把用于铜版印刷的美术用纸叫做铜版（印刷）纸。在中国内地的印刷，纸业界的同仁们，都把Art Paper称为铜版纸，而不叫粉纸。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;顺便说一下，有些书刊上把铜版纸译为Copper Plate Paper，或者Enameled Paper是不够确切的。前者让人误为是像铜板那样的纸，它应译为四版纸，后者应该是蜡光纸（台湾称为丽光纸）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong style=&quot;text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;主要特性&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;供铜版印刷用的一种涂料加工纸。&amp;nbsp; 根据不同用途，可分为单面或双面涂布。有平板纸和卷筒纸。定量一般为70～150g/m2，也有双面涂布达180～250g/m2。纸质均匀紧密，白度较高（85%以上），纸面光滑光泽度高，涂层坚牢一致，便于在印刷后获得精美清晰的图案和文字。铜版原纸用漂白化学木浆或配以部分漂白化学草浆在造纸机上抄造而成。以原纸为纸基，将白色颜料（也称白土，如高岭土、滑石粉、碳酸钙、二氧化钛等）、胶黏剂(聚乙烯醇、干酪素、改性淀粉、合成胶乳等），以及其他辅料（如光泽剂、硬化剂、塑化剂、分散剂、润湿剂、乳白剂、荧光增白剂、调色剂等），在涂布机上进行均匀涂布加工，并经干燥和超级压光而制成。用于高级美术印刷、挂历、招贴画、画册、彩色插图、书刊封面和广告商标等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;铜版纸的特点在于纸面非常光洁平整，平滑度高，光泽度好。因为所用的涂料白度达90%以上，且颗粒极细，又经过超级压光机压光，所以铜版纸的平滑度一般是600～1000s。同时， 涂料又很均匀地分布在纸面上而显出悦目的白色。对铜版纸的要求有较高的涂层强度，涂层薄而均匀、无气泡 ，涂料中的胶粘剂量适当，以防印刷过程中纸张脱粉掉毛，另外，铜版纸对二甲苯的吸收性要适当，能适合60线/厘米以上细网目印刷。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;大家看到的漂亮的挂历、书店中销售的张贴画、书籍的封面、插图、美术图书、画册等等。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong style=&quot;text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;工艺技术&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;印刷铜版纸的工艺技术要求：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;由于一些国产铜版纸因工艺上的缺陷，以致容易产生不同程度的脱粉和伸缩变异，特别是单面涂料纸较容易产生卷曲弊病，影响套印的准确度。印刷铜版纸若干艺操作不当，还容易产生粘脏（过底）、粉化、慢干、星色欠光亮等，故应注意以下几个问题。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;对每一批种的铜版纸，投入使用之前，应对纸的平滑度、酸碱度、吸收性、干燥性、伸缩性以及表面结合强度等一系列涉及印刷适性问题，进行测试分析，尔后根据纸的特性，采取相应的工艺措施，如调整油墨的粘度、流动性、燥性及防粘措施，以实现规范化科学生产，防止印刷不良情况发生；&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;要把好垫版技术和印刷压力调试关，确保印刷墨层均匀和光亮。铜版纸印刷压力应求得均匀又适当，压力过重容易产生掉粉不良情况，且易造成网点印迹不清晰弊病，以及加大印版磨损。反之，则易造成网点印迹不结实，以及盲目加大油墨层来弥补压力不足引起的版面发花，那样，易产生粘脏、慢干、叠印不上等不良后果，故印刷铜版纸压力要均匀，要适当。包衬则采用中性偏硬材料为宜。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;用铜版纸印刷商标、包装产品，常以淡色作底色，经叠色套印而成。这样印刷右工艺不当，也易产生一些问题而影响质量，所以，在调配底色墨时，应该尽量选用耐热和耐光的油墨，并且冲淡剂最好用亮光浆调配，这样可以将底色放到最后印效果要好，以防止底色先印易磨擦损伤墨层。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;由于铜版纸表面光滑，吸墨能力较差，印刷大版面的实地版时，应尽量做到“深墨薄印”。即把油墨相适当调深些，这样印刷时墨层相应或薄一些，即能符合印刷色相要求，又能减少发生印迹过底的机会，并可防止叠色印不上。此外，由于纸张附着力差，印上去的墨层，有时容易因掉粉而脱色，为此，印第一色可用19号树脂油作调墨油。为使使叠色墨层印得光亮，可在油墨中适当加些亮光油和燥油。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;铜版纸由于涂料多，印刷过程中经常会遇到脱粉和印品版面呈现黑影现象。有这种情况时，应注意检查装版工艺操作是否，如印版有无垫得偏重情况，因为纸面压力过大的话，很容易将铜版纸表层的涂料粉质压碎，引致脱粉和黑影斑点。另外，还应检查油墨粘稠度是否合适，若印刷油墨层过厚度、过粘，也是造成纸面掉粉而堆积版面的原因之一，有此情况时，应考虑应用辅助材料对油墨加以调整质量，上述质量问题则可得到较好的克服。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;要把好铜版纸印刷的防粘工艺技术关。铜版纸吸墨性弱，背面又比较光滑，这样印刷受墨量大的版面或多色叠印的，容易产生粘脏问题，故应采取防粘措施加以克服，如采取以亮光油先把适量的防粘剂搅均匀，使之成液体状后加入油墨中再搅匀，印刷时就可收到较好的防粘效果。但防粘剂的用量应注意合理掌握，不能超过4%，以免使油墨乳化、印刷墨层发花以及墨层光泽度下降等不良弊病产生。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong style=&quot;text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;主要用途&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;布纹铜版纸是用旧毛毯压过的，它除了纸面平整、白度高外，还使印出的图形、画面具有立体感，因而这种铜版纸可广泛地用来印刷画报、广告、风景画、精美挂历、人物摄影图等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px; text-indent: 2em;&quot;&gt;铜版纸是印刷厂主要使用的纸张之一，铜版纸是俗称，正式名称应该是印刷涂料纸，在现实生活中运用很普遍。大家看到的漂亮的挂历、书店中销售的张贴画、书籍的封面、插图、美术图书、画册等等，几乎都是用的铜版纸，各种精美装潢的包装、纸质手提包、标贴、商标等也大量的使用到铜版纸。铜版纸是由涂料原纸经涂布和装饰加工后制成的纸张，表面平滑细致，涂布有双面、单面之分，纸张分有光和无光（亚光）铜版纸。常用铜版纸分为从每平方米105克到每平方米350克的多种厚度规格。铜版纸销售中单张纸张尺寸一般分为大度（889mm×1194mm）、中度（787mm×1092mm）两个尺寸规格。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong style=&quot;text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;为什么用铜版纸印刷高级印刷品&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;铜版纸是随着网线版印刷发展起来的，由于未经涂布的纸张纤维组成的表面凹凸不平和程度 比网点的直径要大得多，某些网点恰好在纤维间的间隙而无法把油墨转移到纸张上，使图像 像素在这个部位无法再现。纸张经涂布浆料再经压光后，纸张表面的颗粒小于网点的直径，浆料受墨性也优于纤维素纤维，因此，网点能够正确复制出来。显然，经过涂布的铜版纸的 印刷性能优于未经涂布的胶版纸。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;铜版纸原纸采用优质卷筒新闻纸或胶版印刷纸，高档的铜版纸采用特制的原纸。表面涂层牢固地粘着在原纸上。纸面再经超级压光，涂层牢固，无气泡、无条痕、光泽性强、平滑度高。铜版纸的平滑度一般在600s以上，比胶版纸高10倍以上。这样，使得铜版纸能与印版紧密 地接触，清晰地印出网点来。铜版纸的pH值在7左右，对平版印刷润湿液无不良影响，对油 墨干燥&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;过程无不良影响。在铜版纸上油墨附着快、干燥快，干燥后还可以衬托出光泽来，网 点再现性好，墨层薄而图像清晰、网点光洁。铜版纸的尺寸稳定性也要优于胶版纸，在铜版 纸上印刷，能够达到套印精确的要求。铜版纸的抗张强度及表面强度也很高，足以抵抗所用 的较粘油墨墨膜产生的较大拉力。由于上述各点，常用铜版纸印刷高级印刷品。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong style=&quot;text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;补充说明&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;铜版纸包含光粉铜版纸和哑粉铜版纸，通俗上哑粉纸多指哑粉铜版纸，铜版纸多指光粉铜版纸；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;两者印刷价位相同，哑粉纸的印刷工艺要求稍高，容易造成印刷过底；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;“哑粉纸”的表面不反光，吸墨性好，印刷油墨相对较厚，所以阴干时间要长些；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;但是如果用UV油墨印刷则可以完全解决阴干时间长的问题（这种油墨比普通油墨稍贵）。&lt;/span&gt;&lt;/p&gt;','admin',1,0,'/uploads/20181214/19a122bab33d8f9d535451481af07f64.jpg',NULL,0,0,0,'2018-09-13 10:27:44','2018-12-18 12:15:10','2018-09-13 10:26:45'),(10,'轻型纸',NULL,'',1,'轻型纸简介','&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体, SimSun;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;简介&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;轻型纸即轻型胶版纸，是一种更人性化的纸种，纸品中的一枝新秀，它质优量轻、价格低廉，不含荧光增白剂，高机械浆含量，环保舒适。采用的原色调可以保护读者尤其是老人和儿童的眼睛，使他们在阅读时保护视力不受伤害， 便于读者携带和阅读，具有天然特性。轻型纸的质感和松厚度好，不透明度高，印刷适应性和印刷后原稿还原性好，质感好且量轻而厚，用其印制的图书比用普通纸印制的图书重量约减轻四分之一到三分之一，方便了读者，也节约了运输和邮购费用。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;轻型纸音译叫法，在瑞典的蒙克达尔（Munkedal），当地的一家造纸企业生产的型轻纸张就地取名也叫Munkedal，当这种纸首次被引进到中国时，它便有了“蒙肯”这个名字。由于它是我国最早引进的轻型胶版纸，所以国内便习惯性的称这一类纸为蒙肯纸(轻型纸)，从事纸业的人士一般直接称其为“蒙肯”。在欧美及日本等经济发达的国家，书店里95%以上的图书是用这种纸印刷。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;font-size: 14px; text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun;&quot;&gt;特点&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;1.轻厚、轻型。轻型纸的松厚度非常好，所以此种纸可以以同等厚度替代原来高定量的纸张，从而节约运费和邮资成本。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;2.颜色自然。轻型纸由化学浆制成，不含荧光增白剂，因加入特殊染料而呈奶白色或淡米色，相比起普通的铜版纸、胶版纸，轻型纸的颜色较暗，与木浆原色相近。给人一种古朴、自然的感觉，长时间阅读用轻型纸印刷的书刊不会造成视觉疲劳。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;3.纸张表面细腻光滑。用手触摸轻型纸会感到其纸质的轻柔细腻，因为没有涂布，使得轻型纸的反光率不高而对油墨的吸收能力却较强。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;4.纸张寿命长且环保。轻型纸为无氯纸种，100%纯化学浆抄制，不含荧光增白剂等成份，从而使纸张呈微碱性，可以保存上千年而不变质。轻型纸未经涂布处理，主要由纤维、碳酸钙和水组成，不污染环境，属绿色环保产品。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 0em;&quot;&gt;轻型纸一般以系数表示其松厚指数，如1.5、1.7、1.8等.指纸张的厚度除以定量!&amp;nbsp;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;“轻型纸”与双胶纸相比，松厚度可达1.90cm3/g,双胶纸松厚度为1.14cm3/g,因此，在印刷时可以用低克重的“轻型纸”替代高克重的双胶纸。以80g/m2胶版纸为例：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;双胶纸厚度80g/m2×1.14cm3/g=91.2μm在同样厚度下的“轻型纸”克重为91.21μm÷1.9cm3/g=48g/m2相当于50g/m2的“轻型纸”.即定量为50g/m2、松厚度为1.9cm3/g的“轻型纸”与定量为80g/m2、松厚度为&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;1.14cm3/g的双胶纸厚度相同。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong style=&quot;font-size: 14px; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun;&quot;&gt;“轻型纸”的市场优势&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;（1）国产“轻型纸”与进口同类纸张相比，价格更低廉。只有进口价格的65%左右。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;（2）“轻型纸”的纸面挺刮，手感柔和，用于书刊印刷时，其外表感观、阅读、使用、保存均能达到理想效果。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;（3）“轻型纸”的纸张色泽顺应了国际纸张白度不高于84度的环保潮流，采用原色调，可以保护人类尤其是儿童和老年人的眼睛，使他们在阅读时更舒适，不刺眼，不疲劳，不受伤害，对保护视力有一定作用。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;（4）“轻型纸”和双胶纸相比印刷效果更好、成本更低。“轻型纸”有一定的松厚度，可用60g/m2的“轻型纸”替代80g/m2双胶纸印刷书本，降低了书本重量。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;（5）由于“轻型纸”采用中性施胶，使用“轻型纸”印的书刊不易返黄，质感好，量轻且厚。&lt;/span&gt;&lt;/p&gt;','admin',1,0,'/uploads/20181214/53e2fb0bdfa3644ea42193e860cc0278.jpg',NULL,0,0,0,'2018-09-13 10:29:11','2018-12-18 12:14:30','2018-09-13 10:28:32'),(11,'纯质纸',NULL,'',1,'纯质纸的简介','&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&lt;strong&gt;简介&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;以100%进口优质木材纤维精制而成，产品具有先进的环保理念的纸张所有的天然特性。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;纸质超轻超厚，经济实用。轻型纸为米色，纯质纸为奶油色。其平滑而无光泽的表面使印刷更具立体感。用其印刷的书籍，纸质轻柔，色彩温和，其超低的反射率和扑面而来的自然气息。&lt;/span&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px; text-indent: 2em;&quot;&gt;产品具有先进的环保理念的纸张所有的天然特性，色彩温和，经济实用。&lt;/span&gt;&lt;/p&gt;','admin',1,0,'/uploads/20181217/749799f366d3a7332d93cf59ff016155.jpg',NULL,0,0,0,'2018-09-13 10:30:41','2018-12-18 12:13:17','2018-09-13 10:29:18'),(12,'轻涂纸',NULL,'',1,'轻涂纸的文化的简介','&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;简介&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;轻涂纸，又叫充分纸，英文名为Light Weight Coated Paper.它是指一类克重较低的涂布加工纸，一般在40-90g/m2。这种纸的正反面都涂布了一层薄薄的涂料（由碳酸钙和阳离子淀粉组成），再经过超级压光，以改善它的物理性能和印刷适性。这种纸的化学成分中，针叶木长纤维漂白化学浆占40%；机械木浆占60%。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸的性能&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;1．性能介于铜版纸与胶版纸间，其彩色印效果可与铜版纸相媲美，它的不透明度、平滑度、光泽度和印刷适性都很好。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2．成本低。它的成本介于铜版纸与胶版纸之间。但是，印刷质量可以达到与铜版纸相媲美的效果，所以属高附加值纸。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;3．不过这种纸的耐久性小，所以印品不易长久保留。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;用途&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;主要用于印刷杂志、商品目录、广告、商标、报刊插页等方面。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;规格&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;卷筒：756 787 889&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;平张：787*1092 889*1194&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;常见克重&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;48g 50g 58g 64g 70g 80g&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;现状&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;国内外印刷用纸中，轻涂纸已经成为一个热门的话题。目前，内地已有十多条轻涂纸生产线。但是，国内轻涂纸在质量上与国外同类纸还有一定距离。&lt;/span&gt;&lt;/p&gt;','admin',1,0,'/uploads/20181217/168415de195bb9f8a10ed0feb3dc10cd.jpg',NULL,0,0,0,'2018-09-13 10:31:06','2018-12-18 12:12:58','2018-09-13 10:30:47'),(13,'字典纸',NULL,'',1,'字典纸','&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;简介&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;字典纸是一种高级的薄型书刊用纸，纸薄而强韧耐折，纸面洁白细致，质地紧密平滑，稍微透明，有一定的抗水性能。主要供印刷字典、词典、经典书籍等一类页码较多、使用率较高、便于携带的书籍。字典纸对印刷工艺中的压力和墨色有较高的要求，因此印刷时必须从工艺上特别重视。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;重量：30～40克/米²。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify; text-indent: 0em;&quot;&gt;&lt;span style=&quot;font-size: 14px;&quot;&gt;平板纸规格：787×1092毫米。&lt;/span&gt;&lt;/p&gt;','admin',1,0,'/uploads/20181217/2f1599cca3b9e123946f4ed2a37ebd4b.jpg',NULL,0,0,0,'2018-09-13 10:33:47','2018-12-18 12:12:42','2018-09-13 10:31:12'),(14,'新闻纸','','',1,'新闻纸的主要介绍','&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;简介&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;新闻纸也叫白报纸，是报刊及书籍的主要用纸。适用于报纸、期刊、课本、连环画等正文用纸。新闻纸的特点有：纸质松轻、有较好的弹性；吸墨性能好，这就保证了油墨能较好地固着在纸面上。纸张经过压光后两面平滑，不起毛，从而使两面印迹比较清晰而饱满；有一定的机械强度；不透明性能好；适合于高速轮转机印刷。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;新闻纸是以机械木浆（或其他化学浆）为原料生产的，含有大量的木质素和其他杂质，不宜长期存放。保存时间过长，纸张会发黄变脆，抗水性能差，不宜书写等。必须使用印报油墨或书籍油墨，油墨粘度不要过高，平版印刷时必须严格控制版面水分。由于其所用原材料以机械木浆为主，含有本质素及杂质，所以纸张不宜长期保存，而且容易破损。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;含义&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（一）印刷报纸和一般书刊用纸。通常以80%以上的机械木浆和20%以下的化学木浆制成。也有以甘蔗渣浆、竹浆为主要原料的。依浆料配比的不同分1号和 2号两种。1号新闻纸技术指标较高，具有较好的吸墨性能和抗张强度，适于高速轮转机印刷。由于机械木浆纤维内含有较多的木质素，在光的作用下&lt;/span&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;纸张易泛黄变脆，故不宜印刷需长久保存的书刊。出厂成品基本为卷筒纸,幅宽有1575、1562、787、781,765毫米等规格，也可根据需要裁切成单张纸供应。&lt;/span&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;在世界范围内，新闻纸用量最多。由于报纸印刷由凸印向胶印发展，造纸原料也从一般磨木浆（即机械木浆）发展为热磨机械浆、化学热磨机械浆等。蔗渣浆造纸在中国南方产糖地区正日益发展。造纸机车速的提高和幅宽水平也以新闻纸为最：车速在1000米/分以上的已屡见不鲜 ，幅宽普遍为 6～8米 ；最高车速达 1500米/分,幅宽10米左右。新闻纸的定量,国际上按印刷要求区别：胶印新闻纸一般为40克/平方米，轮转凹印新闻纸约50克/平方米。中国轮转凸印新闻纸传统定量为51～52克/平方米 ，并趋向于低定量 ，发展规格为48克/平方米的纸张。中华人民共和国成立后，东北和南方各省相继扩建和新建一批大型造纸厂，新闻纸初步接近供需平衡。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;（二）指正规的活字印刷的报纸，英文为“newspaper”，十七世纪初出现此词，中国近代译为报纸的同义词。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;（三）俗称白报纸，主要用于印刷报纸、期刊以及印数较多、使用期限较短的书刊资料、胶印连环画等出版物。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;特点&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;新闻纸的特点有：纸质松轻、有较好的弹性；吸墨性能好，这就保证了油墨能较好地固着在纸面上。纸张经过压光后两面平滑，不起毛，从而使两面印迹比较清晰而饱满；有一定的机械强度；不透明性能好；适合于高速轮转机印刷。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;重量&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（45～52）±0.5g/m^2.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;规格&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;平板纸规格：787×1092，850×1168，880×1230，889×1194.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;卷筒纸规格：宽度390，720，781，860，1440，1562，长度约6000～13000m.&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;吸水性&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;新闻纸的吸水量较大，印刷过程中纸张的吸水伸缩是影响彩报套印精度的主要因素，如何控制和消除印刷过程中纸张吸水伸缩对套印精度的影响，特别是在设备和原材料条件不理想的报社，这个问题尤为重要。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;吸湿变形&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;新闻纸纤维较粗、质地疏松，吸水伸长程度较铜版纸、胶版纸更大一些；而彩报印刷普遍采用有水胶印工艺，印刷过程中必然存在纸张因吸水而发生伸缩变形，对套印精度有较大的影响。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;①新闻纸的横向吸水伸缩对套印精度影响最为明显。在彩报印刷中，新闻纸的丝缕方向与纸带运动方向一致。顺丝缕的纵向伸缩量较小，大约为横向的1/3～1/2，一般通过调整机组间的套准调节辊可以消除，对套印精度影响不大；横向的伸缩变化在没有采取一定消除措施的情况下套印误差较大（可达到0.3mm以上），极大地影响了彩报的印刷质量。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;②印刷单元组合形式不同，吸水伸缩对套印精度的影响程度也不同。彩报的印刷过程是对纸张依次印刷四色油墨，机组式报业轮转胶印机因纸路较长，新闻纸吸水伸缩变形大，对套印精度影响较明显；塔式报业轮转胶印机因纸路较短，新闻纸吸水伸缩变形较小，对套印精度影响较小。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;应对措施&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;①控制印刷过程中润版液的用量。彩报印刷中，第一色组的水量是决定新闻纸吸水伸缩对套印精度影响的主要因素，因此，要很好地控制第一色组润版液用量，在保证彩报印刷质量的前提下使用最小水量。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;②卷筒新闻纸不可过于干燥，否则吸水量也较大，伸缩率较高，吸水伸缩对套印精度影响也大；而且新闻纸变脆，印刷时易断纸；过于干燥的新闻纸在印刷过程中也易带静电，影响折报、收报、堆集工作。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;③最好彩用塔式报业轮转胶印机，因为这样纸张四色印刷之间的纸路较短，新闻纸吸水伸缩变形对套印精度影响较小，有利于提高套印精度。当前还有不少地市报社印刷厂还在使用机组式报业轮转胶印机，机组间纸路较长，又无扇形扩张装置，在印刷彩报时纸张的吸水伸缩对套准精度影响十分明显。在这种情况下，根据印刷中纸张伸缩量的大小，可以通过拼版时改变胶片位置的方式来消除纸张吸水伸缩对套印精度的影响。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;国家标准&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;《新闻纸》GB/T 1910-2015于2016年7月1日实行，替代GB/T 1910-2006。&lt;/span&gt;&lt;/p&gt;','admin',1,0,'/uploads/20181217/05f5a846845b321e8ac464b5ccd90d2f.jpg',NULL,0,0,0,'2018-09-13 10:38:52','2018-12-18 12:11:58','2018-09-13 10:37:00'),(15,'特种纸','','',1,'特种纸','&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;简介&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;特种纸是将不同的纤维利用抄纸机抄制成具有特殊机能的纸张，例如单独使用合成纤维，合成纸浆或混合木浆等原料，配合不同材料进行修饰或加工，赋予纸张不同的机能及用途，例如：生活用、建材用、电气制品用、工业过滤器用、机械工业用、农业用、信息用、光学用、文化艺术用、生化尖端技术用等等，只要是用于特殊用途的纸材，全部统称为特种纸。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;发展过程&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;特种纸（specialtypaper）这个名称是20世纪60年代以后才逐步流传起来的。也有人叫它特种加工纸。特种纸源于1945年美国国立现金出纳机公司（NationalCashRegisterCo.）研制成功的无碳复写纸。后来，其它特种纸不断地推出，拓开了人们的视野，改变了人们对纸的传统看法。原料上也不再限于植物纤维，非植物纤维（如无机纤维、合成纤维）也得到了应用。结构上，除了纤维交织层外，还能加工或者增加涂布层数。效果上，可根据需要按设计来抄造，这都是过去没有想到的。但是，发展特种纸面临不少困难，如资金投入多、设备要求高、技术难度大等。同时，不少特种纸的应用面较窄，需求量小。短期内没有形成气候，有待于人们去开拓。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;目前国产特种纸的产量很低，远远未能满足中国特种纸的使用需要。但由于特种纸应用范围广泛，性能及附加价值高，国内中小型生产特种纸的纸厂，应用学术研究单位的技术支持不是很普遍，使得中国特种纸发展有一定的障碍。特种纸生产企业应该做适当的生产规划，朝着产品转型和技术开发的方向迈进，相信一定能建立国产特种纸产业生存及发展的良好空间。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;种类&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;棉纸、宣纸、隔层纸、无尘纸、隔油纸、防尘纸、防锈纸、钞票纸、薄页纸、喷墨纸、擦拭纸、热敏纸、描图纸、过滤纸、浮印纸、吸油纸、茶袋纸、铝箔纸、拷贝纸、美术纸、障子纸、透气纸、羊皮纸、复写纸、毛边纸、离型纸原纸、无碳复写纸、壁纸原纸、玻璃卡纸、咖啡滤纸、剥离纸、生理用纸、烟卡、格拉辛纸、壁纸被覆材、美耐板用纸、吸湿纸、耐燃纸、石棉纸、柏油纸、防霉纸、静电防止纸、导电纸、半导电纸、电池分离纸、电磁遮蔽纸、电气绝缘纸、耐热纸、汽车用滤纸、空调滤纸、脱臭滤纸、医疗卫生用纸、药包纸、无菌纸、医疗胶布基材、手术衣、研磨纸、蚕棉纸、温床用纸、被覆纸、遮光纸、育苗纸、防虫纸、感压记录纸、印刷电路板厚纸、感热记录纸、磁性记录纸、磁性卡纸、热转写记录纸、电子基层板用纸及其它各种信息用纸、相片用纸、拭镜纸、印画纸、纸绳、硫酸纸、书画用纸、调湿纸、墨流纸、化妆纸、酵素固定化用纸、吸着纸、纺筒纸等等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;印刷用特种纸&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;1.植物羊皮纸（硫酸纸）&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;植物羊皮纸是把植物纤维抄制的厚纸用硫酸处理后，使其改变原有性质的一种变性加工纸。呈半透明状，纸页的气孔少，纸质坚韧、紧密，而且可以对其进行上蜡、涂布、压花或起皱等加工工艺。其外观上很容易和描图纸相混淆。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;因为是半透明的纸张，硫酸纸在现代设计中，往往用做书籍的环衬或衬纸，这样可以更好地突出和烘托主题，又符合现代潮流。有时也用做书籍或画册的扉页。在硫酸纸上印金、印银或印刷图文，别具一格，一般用于高档画册较多。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2.合成纸（聚合物纸和塑料纸）&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;合成纸是以合成树脂（如PP、PE、PS等）为主要原料，经过一定工艺把树脂熔融，通过挤压、延伸制成薄膜，然后进行纸化处理，赋予其天然植物纤维的白度、不透明度及印刷适性而得到的材料。一般合成纸分为两大类：一类是纤维合成纸，另一类是薄膜系合成纸。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;合成纸在外观上与一般天然植物纤维纸没有什么区别，薄膜系合成纸已经打入高级印刷纸的市场，能够适应多种印刷机。现在市场上所用的合成纸仅是指薄膜系合成纸。用合成纸印刷的书刊、广告、说明书等如果不标明，一般消费者是看不出它与普通纸有什么区别的。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;合成纸有优良的印刷性能，在印刷时不会发生“断纸”现象；合成纸的表面呈现极小的凹凸状，对改善不透明性和印刷适性有很大帮助；合成纸图像再现好，网点清晰，色调柔和，尺寸稳定，不易老化。合成纸的印刷效果如图3所示。值得注意的是，采用胶印印刷时，应采用专业的合成纸胶印油墨。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;3.压纹纸&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（1）压纹纸的特点&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;采用机械压花或皱纸的方法，在纸或纸板的表面形成凹凸图案。压纹纸通过压花来提高它的装饰效果，使纸张更具质感。近年来，印刷用纸表面的压纹愈来愈普遍，胶版纸、铜版纸、白板纸、白卡纸等彩色染色纸张在印刷前压花（纹），作为“压花印刷纸”，可大大提高纸张的档次，也给纸张的销售带来了更高的附加值。许多用于软包装的纸张常采用印刷前或印刷后压纹的方法，提高包装装潢的视觉效果，提高商品的价值。因此压纹加工已成为纸张加工的一种重要方法。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（2）压纹纸的加工方法&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;压纹纸的加工方法有两种。一为纸张生产后，以机械方式增加图案，成为压纹纸；二为平张原纸干透后，便放进压纹机进一步加工，然后经过两个滚轴的对压，其中一个滚轴刻有压纹图案，纸张经过后便会压印成纹。由于压纹纸的纹理较深，因此通常仅压印纸张的一面。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（3）压花种类&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;压花可以分为套版压花和不套版压花两种。所谓套版压花，就是按印花的花形，把印成的花形压成凹凸形，使花纹鼓起来，可起美观装饰的作用。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;不套版压花，就是压成的花纹与印花的花型没有直接关系，这种压花花纹种类很多。如布纹、斜布纹、直条纹、雅莲网、橘子皮纹、直网纹、针网纹、蛋皮纹、麻袋纹、格子纹、皮革纹、头皮纹、麻布纹、齿轮条纹等。这种不套印压花广泛用于压花印刷纸、涂布书皮纸、漆皮纸、塑料合成纸、植物羊皮纸以及其他装饰材料。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;国产压纹纸大部分是由胶版纸和白板纸压成的。表面比较粗糙，有质感，表现力强，品种繁多。许多美术设计者都比较喜欢使用这类纸张，用此制作图书或画册的封面、扉页等来表达不同的个性。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;4.花纹纸&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;设计师及印刷商不断寻求别出心裁的设计风格，使作品脱颖而出。许多时候花纹纸就能使它们锦上添花。这类优质的纸品手感柔软，外观华美，成品更富高贵气质，令人赏心悦目。花纹纸品种较多，各具特色，较普通纸档次高。花纹纸可以分为以下几种：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（1）抄网纸&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;抄网是令纸张产生纹理质感的最传统和常用的方法。这个工序一般加插在造纸过程中，湿纸张被放在两张吸水软绒布之间，绒布的线条纹理便会印在纸张上。这项工序分单面或双面印纹。像刚古条纹纸等抄网纸的线条图案若隐若现、质感柔和，有些进口的抄网纸均含有棉质，因此质感更柔和自然而且韧度十足，适宜包装印刷。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（2）仿古效果纸&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;不少客户及平面设计师对优质、素色的非涂布纸情有独钟，最爱这类纸温暖丰润的感觉。此外，书籍出版商也要求较耐用的纸张，更希望纸质清爽硬挺，确保印刷效果稳定一致。用仿古效果纸设计出的产品古朴、美观、高雅。如图7所示。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（3）掺杂物及特殊效果纸&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;环保潮流大行其道，斑点纸应运而生，为了达到天然再造的效果，纸浆分别加入了多种杂物。所掺杂质的分量、大小等都要控制得适宜，假如纸张的杂点数目太多，便会破坏图文效果，太少则会给人粘脏的感觉。现今市场上备有多种掺杂质的纸张供用户选择，有的环保再生纸加有矿石、飘雪、花瓣等杂质。其抄造技巧则包括在施胶过程中添加染料，使纸张形成斑点或营造羊皮纸效果。这类纸张深受消费者欢迎，更是各种证书、书籍封面及饭店菜谱及酒水单的首选。由于具备特殊效果的纸张外观，设计师则经常用于简单的印刷品及枯燥无味类文稿印刷的首选纸，例如上市公司年度报告的各种财务报表等，它能使版面活跃起来，吸引眼球。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（4）非涂布花纹纸&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;印刷商和设计师都一致推崇美观且手感良好的非涂布纸，为了使印刷品达到美好的视觉效果，更具有高档华丽的感觉。非涂布抄网纹纸的自然质感和涂布纸的印刷适性效果兼收并蓄。这种纸的两面均经过特别处理，令纸的吸水率降低，以致印墨留在纸张表面，使油墨的质感效果更佳。而金属珠光饰面系列纸，可随着人眼观看视角的不同而发生变化，印刷后的效果更佳。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（5）刚古纸&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;刚古品牌的特种纸，创于公元1888年，最初采用CONQUEROR伦敦城堡水印，直至20世纪90年代初才改为骑士加英文水印。这个饶有特色的水印举世闻名，至今已成为高品质商业、书写、印刷用纸的标志和代号，行销全球80多个国家和地区，深受各地酒店、银行、企业甚至政府机构喜爱。刚古纸分为贵族、滑面、纹路、概念、数码等几大类。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（6）“凝采”珠光花纹纸&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸张的色调可根据观看角度的变化而产生不同的色彩感觉。它的光泽是由光线弥散折射到纸张表面而形成，具有“闪银”效果，因此印刷具有金属特质的图案将会非常出色。它适合制作各类高档精美富有现代气息的时尚印刷品。高档图书的封面或精装书的书壳。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸面平顺亮滑，适合各类印刷工艺及加工手段，如四色胶印、轧切、烫印等。建议采用氧化结膜干燥型油墨，油墨的光泽度可能会不如纸张本身的光泽度。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（7）“星采”金属花纹纸&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;金属“星采”花纹纸是一种突破传统、全新概念的艺术纸。它不仅保持了高级纸张所固有的经典与美感，还独具创意地拥有正反双面的金属色调，华贵而不俗气，稳重而不张扬，使其显现出迥异于一般艺术纸的强烈气质。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;金属“星采”由于采用了新工艺，其金属特质绝不脱落，纸面爽滑，反而为印刷效果增添了无穷的魄力，它适用于各类印刷技术及特殊工艺，尤其是烫印工艺的表现。印刷时建议网线在130～150线/英寸，在深色金属花纹纸上印刷时应将墨色加重。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;金属“星采”可制作各种高档印刷品，如用做产品样本的纸张、年报和书籍的封面及供各种包装盒使用。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;（8）金纸&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;金纸与传统金箔具有质的差别，传统金箔只能借助于透明材质形成印刷品，不能直接着色印刷。用24K黄金为材料，运用纳米高科技研制的金纸，既能使彩色图像直接印刷于黄金之上，又能保留黄金的风采与性能，具有抗氧化、抗变色、防潮、防蛀的特性，避免了传统纸张书籍易霉变和虫蛀的缺点，理论上可存放万年之久。《中华盛世》所用金纸是最新研制的新一代产品，具有更高的科技含量和文化含量，其表状更类似古代宣纸与新闻报纸，特别是其阻燃性首次获得重大突破。正文20个金版以《经济日报》版式四色印刷，图文并茂，共计近16万字，300张人物、风光等金版图片，金纸用量和版面之大前所未有，这在金版印刷史上规模空前。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;不足之处&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;第一，价格较贵。普通的压纹纸要比同定量的同类纸张贵很多，因此大批量用压纹纸印刷的画册因价格因素客户不能承受，所以一般只用于封面或重要资料的设计及印刷。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;第二，大多数特种纸的吸收性好、渗透力强，但因有凹凸不平的纹路，使得在印刷时油墨渗透在缝隙中，特别是大面积的实地版，很难干燥，容易粘脏。在印刷四色叠印的人物图像时，调子比较沉闷。遇到不喜欢软调子的消费者不理解为什么特种纸印刷出来的产品颜色不够鲜艳的情况时，在采用特种纸印刷时，一定要把印刷效果的问题与客户沟通，以免造成不必要的损失。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;第三，有色特种纸在印刷时，要注意深色的特种纸张表面是不能印四色图像的。只能印刷金属光泽或专色调配的油墨，如金、银、专色等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;如在浅米色、浅绿色、浅蓝色、浅粉色等浅色调特种纸的表面印刷四色网目调图像，就会出现偏色的现象，一般情况调子会偏向纸张表面的颜色。因此，设计时最好不要使用有颜色的纸张印刷网目调图像。但如果能正确使用不同风格和不同颜色的特种纸设计出的作品，会有意想不到的特殊效果。&lt;/span&gt;&lt;/p&gt;','',1,0,'/uploads/20181217/333eafc5d9b8b105cf17a0f26a978287.jpg',NULL,0,0,0,'2018-09-13 10:39:36','2018-12-18 12:10:05','2018-09-13 10:39:04'),(16,'纸浆',NULL,NULL,1,'纸浆','&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;简介&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸浆是以某些植物为原料加工而成的，它是造纸的基本原料。通常用以制浆原料的植物可分为4大类：茎干纤维类、韧皮纤维类、种毛纤维类和木材纤维类。其中尤以木材纤维类为最重要。目前我国木材资源远不能满足日益发展的制浆造纸工业的需要。为了弥补原料的不足，每年要从国外进口相当数量的纸浆。主要进口国为美国、加拿大、巴西。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;国内发展&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2010年，中国造纸行业拥有规模以上企业146家，同比增长2.10%;工业总产值达到324.91亿元，同比增长38.00%;实现销售收入317.49亿元，同比增长40.71%;实现利润总额21.53亿元。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;国内多数造纸企业的特种纸原料大部分依赖进口。2010年，智利强震影响了全球8%的商品浆供应和我国14%的木浆进口，加上造纸强国芬兰的罢工，引发了国际特种纸价格大幅上涨，多个品种造纸的价格都已超过高点。特种纸价格上涨让造纸企业不得不缩减产量，收紧库存。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;预计这种紧缩局面短期内不会扭转。中长期看，造纸价格走势上涨概率还是较大。2011-2015年，国内有意向投产的造纸产能达到4600万吨，预计新增1130万吨阔叶浆需求，380万吨针叶浆需求。而2010-2015年间，阔叶浆新增产能达到1400万吨，针叶浆为150万吨，但是在2011-2012年期间，新增的木浆线不多，大部分在2013年后投产。因此，2011-2012年内造纸价格大趋势是上涨的。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;国际造纸价格保持高位对国内造纸行业产生了微妙影响。在进口原料成本压力下，自制浆比例高的公司在成本控制方面具有一定优势，一些中小造纸企业则会因产能落后遭淘汰。所以，造纸企业如果想要在激烈的竞争中生存下来，可以考虑增加对纸浆行业的投资，那样利于控制成本，增加企业的利润。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;工艺流程&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;传统制浆是指利用化学方法、机械方法或两者结合的方法，使植物纤维原料离解变成本色或漂白纸浆的生产过程。通常采用的工艺过程是将植物纤维原料粉碎、蒸煮、洗涤、筛选、漂白、净化、烘干。　现代新发展出一种生物制浆法，先用特殊菌种（白腐菌、褐腐菌、软腐菌）专门定向分解木素结构，后辅以机械或化学方法解离剩下的纤维素，而后进行漂白。此过程中，生物已经分解、打开大部分木素，化学方法只是作为辅助作用，所用的化学产品较之传统方法很少，因此可以做到少排放或不排放废液，是一种环保制浆、清洁制浆方法。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;分类&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;①按制造方法分为化学浆（包括烧碱法纸浆、硫酸盐法纸浆、亚硫酸盐法纸浆等）、机械浆（包括磨石磨木浆、盘磨机械浆、热磨机械浆等）和化学机械浆3大类；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;②按原料品种分为木浆、草浆、竹浆、棉浆、废纸浆等；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;③按加工深度分为本色浆、半漂浆、漂白浆、精制浆等；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;④按木浆对木材的得率分为化学浆（得率为40～50%）、高得率化学浆（得率为50～65%）、半化学浆(得率为65～84%)、化学机械浆(得率为85～94%)、机械浆（得率为94～98%）；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;⑤按用途将企业自用的纸浆称为自用浆，作为商品出售的称为商品浆。对于某一种具体品种，则常以几种不同分类方法综合命名，例如漂白硫酸盐针叶木浆、本色亚硫酸盐苇浆、杨木磨木浆等，以便于使用者根据其名称而能大体了解纸浆的性能与用途。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在各类纸浆中，化学浆仍占有最大比重。1987年世界化学浆总产量达11135万吨，占总浆产量(15586万吨)的71%，机械浆占21%，化学机械浆因发展较晚，相对产量较少，但增长速度较快。由于废纸回收利用率不断提高及纸张所用填料和涂料的量相对增加，纸浆用量相对减少，因而纸浆的增长速度一般低于纸及纸板的增长速度。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;质量评价&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸浆的品质主要决定于其纤维形态和纤维的纯净程度。这两方面的性质又主要决定于所使用的原料品种以及制造方法和加工深度。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在纤维形态方面，主要为纤维的平均长度，纤维胞壁厚度对胞腔直径的比值以及纸浆中非纤维状杂细胞和纤维束的含有量。一般纤维平均长度大，胞壁厚度对胞腔直径比值小，没有或很少非纤维状杂细胞及纤维束的纸浆具有良好的结合强度、脱水与抄造性能，能生产较强韧的纸张,属于较高级的纸浆,如云杉类针叶木浆、棉麻浆等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;在纤维的纯净程度方面，一般以纤维素含量高而其他组分含量低的纸浆品质为好。这类纸浆具有较高的耐久性，较强的结合力，较高的白度和较好的电气绝缘等优良性能。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;不同用途和不同档次的纸张，对纸浆品质的要求各有侧重，并非都要选择纤维形态最好，纤维纯度最高的纸浆，总的原则是选择既能满足某种纸张的生产和使用质量要求，而价格又最低廉的品种。商业上及生产上常根据不同的使用要求制定各种纸浆的质量检验指标，如纸浆的白度、滤水度、筛分组分、树脂及灰分含量、纤维素含量、硬度（代表木素含量）、浆张的物理强度等影响制成品各项性能的指标。这些指标实际上均系纸浆的纤维形态及其纯净度的具体反映。生产纸张既可以选择某一种适宜的纸浆也可以选择两种或两种以上不同品质的纸浆按适当比例配合。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;种类用途&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;1.机械木浆(MECHANICALWOOD-PULP)&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;机械木浆亦称磨木浆，是利用机械方法磨解纤维原料制成的纸浆。它在造纸工业中占有重要的地位。它的生产成本低，生产过程简单，成纸的吸墨性强，不透明度高，纸张软而平滑。适宜印刷上的要求。但由于纤维短，非纤维素组份含量高，所以成纸强度低。另外由于木材中的木素和其他非纤维素绝大部分未被除去，用其生产的纸张易变黄发脆，不能长期保存。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;机械木浆通常指白色机械木浆和褐色机械木浆两种。白色机械木浆主要用于生产新闻纸，也可配入其他纸浆中抄制书写纸和印刷纸；褐色机械木浆多用于生产包装纸和纸板，特别是工业用纸板。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;2.硫酸盐木浆(SULPHATEWOODPULP)&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;硫酸盐木浆是采用氢氧化钠和硫化钠混合液为蒸煮剂。在蒸煮过程中，因为药液作用比较和缓，纤维未受强烈侵蚀，故强韧有力，所制成的纸，其耐折、耐破和撕裂强度极好。它一般可分为漂白和未漂两种。未漂硫酸盐木浆可供制造牛皮纸、纸袋纸、牛皮箱板纸及一般的包装纸和纸板等。漂白硫酸盐木浆可供制造高级印刷纸、画报纸、胶版纸和书写纸等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;3.亚硫酸盐木浆(SULPHITEWOODPULP)&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;亚硫酸盐木浆是以亚硫酸和酸性亚硫酸盐的混合液为蒸煮剂。该浆的纤维较长，性质柔软，韧性好，强度大，容易漂白，并有极好的交织能力。依其精制程度可分为未漂、半漂和漂白三种。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;未漂浆因含少量木素和有色杂质，所以呈黄色，纤维也较硬，多用于抄造中等印刷纸、薄包装纸以及半透明纸和防油纸。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;半漂浆中含有大量的多缩戊糖，因此抄造透明的描图纸和仿羊皮纸等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;漂白浆的纤维洁白，质地纯洁而柔软，但由于经过漂白处理，纤维强度低于未漂浆。此种浆多用以抄造各种高级纸。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;4.废纸纸浆(WASTEPAPERPULP)&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;废纸纸浆系利用使用过的废纸或印刷厂裁切下的纸边为原料，经过机械力量搅拌并经漂白或脱墨处理而制成的。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;废纸纸浆的纤维强度和性能，是废纸所用的纸浆种类决定的。但是由于纤维再次遭受药液侵蚀，或受机械力的损伤，所以较原来纤维性质为差。根据废纸纸浆原废纸的质量高低，分别用于抄造印刷纸、书写纸、纸板及较低档的纸张。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;除上述介绍的几种纸浆外，还有稻草浆、苇浆、蔗渣浆、竹浆、棉浆、麻浆及合成纸浆等。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸浆成分&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸浆中构成纤维的主要成分为纤维素，另外还有不同程度的半纤维素、木素、树脂、色素、果胶和灰分等物质。其中纤维素和半纤维素是纸浆需要的基本成分，而其它成分均属除掉之列。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;主要指标&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;纸浆的性能指标甚多，概括起来可分为物理和化学性能两大类。而物理性能是判断纸浆价值的重要指标，其中抗张强度、耐破强度和撕裂强度是进口纸浆的主要考核指标。进口量较大的是漂白硫酸盐木浆、未漂硫酸盐木浆、漂白亚硫酸盐木浆和机械木浆。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;检验标准&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;进口纸浆检验主要包括销售重量检验和物理性能检验，所涉及的检验标准有美国标准(TAPPI)、国际标准(ISO)、北欧标准(SCAN)、澳大利亚标准(APPITA)、加拿大标准(CPPA)等。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;消耗结构&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;背景&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;随着中国人民生活水平的改善和造纸工艺水平的不断提高，中国的纸产品呈现高档化和多元化趋势。进口纸品有着进一步高档化的趋势，而国产纸品的产品线进一步拓宽，逐渐填补了先前的很多空白，对于优质长纤维的需求比重也呈逐渐上升趋势。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;结构变化&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体, SimSun;&quot;&gt;尽管林浆纸一体化是中国造纸工业解决原木浆供应瓶颈、改善纸品档次和竞争力的重要出路，但是由于人口压力，林地资源有限，一体化工程投入资本较高，原木供应周期较长等等诸多不利因素，中国的林纸一体化工程未来几年很难达到在加拿大、芬兰等国相应行业贡献度的水平。因此，仅仅依靠造林来提高中国造纸工业纸浆中原木浆的比重，短期内难度很大。尽管原木浆占纸浆的比重变化不大，但是废纸浆所占比重却是逐年增长。&lt;/span&gt;&lt;/p&gt;','',1,0,'/uploads/20181217/70f47c07e9c9d73d2794dfabf9137a4b.jpg',NULL,0,0,0,'2018-09-13 10:43:11','2018-12-18 12:08:17','2018-09-13 10:42:55');

/*Table structure for table `os_slide` */

CREATE TABLE `os_slide` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `cid` int(10) unsigned NOT NULL COMMENT '分类ID',
  `name` varchar(50) NOT NULL COMMENT '轮播图名称',
  `description` varchar(255) DEFAULT '' COMMENT '说明',
  `link` varchar(255) DEFAULT '' COMMENT '链接',
  `target` varchar(10) DEFAULT '' COMMENT '打开方式',
  `image` varchar(255) DEFAULT '' COMMENT '轮播图片',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '状态  1 显示  0  隐藏',
  `sort` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 COMMENT='轮播图表';

